Method.Source:
[Form]
public class WMSBillOfLading extends FormRun
{
    //<GEERU>
    #ISOCountryRegionCodes
    //</GEERU>

    NumberSeq       numberSeq;

}

public void close()
{
    if (!wmsBillOfLading.RecId && numberSeq)
    {
        numberSeq.abort();
    }

    super();
}

public void enableFields()
{
    wmsBillOfLading_ds.object(fieldNum(WMSBillOfLading, MasterBOLId)).allowEdit(!wmsBillOfLading.MasterBOL);
    wmsBillOfLading_ds.object(fieldNum(WMSBillOfLading, MasterBOLId)).skip(wmsBillOfLading.MasterBOL);
    // <GEERU>
    wmsBillOfLading_ds.object(fieldNum(WMSBillOfLading, CarrierCode_RU)).allowEdit(wmsBillOfLading.CarrierType_RU != CarrierType_W::InvoiceAccount);
    WMSBillOfLading_ds.object(fieldNum(WMSBillOfLading, TransportationPayer_RU)).allowEdit(WMSBillOfLading.TransportationPayerType_RU == TransportationPayerType_RU::ThirdParty);

    if (wmsBillOfLading.WMSReferenceType == WMSReferenceType::Sales &&
        SysCountryRegionCode::isLegalEntityInCountryRegion([ #isoRU ]))
    {
        WMSBillOfLadingPrint.visible(false);
        WMSBillOfLadingPrintMenu.visible(true);
    }
    else
    {
        WMSBillOfLadingPrint.visible(true);
        WMSBillOfLadingPrintMenu.visible(false);
    }
    // </GEERU>

    inventTransRefId.enabled(wmsBillOfLading.WMSReferenceType != WMSReferenceType::None);
}

public void init()
{
    utcdatetime utcdatetimeMinValue = DateTimeUtil::minValue();
    utcdatetime utcdatetimeMaxValue = DateTimeUtil::maxValue();

    super();

    deliveryLogisticsPostalAddress_ds.addFieldToSelectionList(fieldNum(LogisticsPostalAddress, CountryRegionId));
    deliveryLogisticsPostalAddress_ds.query().validTimeStateDateTimeRange(utcdatetimeMinValue, utcdatetimeMaxValue);

    shippingLogisticsPostalAddress_ds.addFieldToSelectionList(fieldNum(LogisticsPostalAddress, CountryRegionId));
    shippingLogisticsPostalAddress_ds.query().validTimeStateDateTimeRange(utcdatetimeMinValue, utcdatetimeMaxValue);

    thirdPartyLocisticsPostalAddress_ds.addFieldToSelectionList(fieldNum(LogisticsPostalAddress, CountryRegionId));
    thirdPartyLocisticsPostalAddress_ds.query().validTimeStateDateTimeRange(utcdatetimeMinValue, utcdatetimeMaxValue);
}

public void run()
{
    boolean enabledBOLId = WMSBillOfLading::isNumberSeqManual();

    wmsBillOfLading_ds.object(fieldNum(WMSBillOfLading, BillOfLadingId)).allowEdit(enabledBOLId);
    wmsBillOfLading_ds.object(fieldNum(WMSBillOfLading, BillOfLadingId)).skip(!enabledBOLId);

    super();
}

private void updateCustJourBillOfLadingId()
{
    Common rec = element.args().record();

    if (!rec)
    {
        return;
    }

    switch (rec.TableId)
    {
        case tableNum(CustPackingSlipJour):
            CustPackingSlipJour custPackingSlipJour = rec;
            custPackingSlipJour.updateBillOfLadingId(wmsBillOfLading.billOfLadingId);
            break;
        case tableNum(CustInvoiceJour):
            CustInvoiceJour custInvoiceJour = rec;
            custInvoiceJour.updateBillOfLadingId(wmsBillOfLading.billOfLadingId);
            break;
    }
}

public int active()
{
    int ret;

    element.enableFields();

    ret = super();

    return ret;
}

public void write()
{
    ttsbegin;
    if (numberSeq)
    {
        numberSeq.used();
    }
    // <GEERU>
    if (wmsBillOfLading.CarrierType_RU == CarrierType_W::InvoiceAccount)
    {
        wmsBillOfLading.CarrierCode_RU = "";
    }
    // </GEERU>
    super();

    ttscommit;

    element.updateCustJourBillOfLadingId();
}

public void delete()
{
    ttsbegin;

    if (!wmsBillOfLading.RecId && numberSeq)
    {
        numberSeq.abort();
    }

    super();

    ttscommit;
}

public void create(boolean append = false)
{
    WMSBOLIdGenerate    wmsbolIdGenerate;

    if (!wmsBillOfLading.RecId && numberSeq)
    {
        numberSeq.abort();
    }

    billOfLadingIdGrid.setFocus();

    super(append);

    if (! wmsBillOfLading.RecId)
    {
        numberSeq = WMSBillOfLading::numberSeq(true,true);
        if (numberSeq)
        {
            wmsbolIdGenerate = WMSBOLIdGenerate::construct();
            wmsbolIdGenerate.parmWMSBillOfLadingId(numberSeq.num());
            wmsbolIdGenerate.makeBOL17();
            wmsBillOfLading.BillOfLadingId  = wmsbolIdGenerate.parmWMSBillOfLadingId();
        }
    }
}

public void lookup(FormControl _formControl, str _filterStr)
{
    if (!wmsBillOfLading.lookupInventTransRefId(_formControl))
    {
        super(_formControl, _filterStr);
    }
}

public void modified()
{
    super();

    if (wmsBillOfLading.MasterBOL)
    {
        wmsBillOfLading.MasterBOLId = '';
    }

    element.enableFields();
}

public void modified()
{
    super();
    wmsBillOfLading.inventTransRefId = '';
    element.enableFields();
}
