[Form]
public class WMSLocation extends FormRun
{
    boolean                         parmLocationType;
    WMSLocationType                 wmsLocationType;
    WMSLocationTreeClient           wmsLocationTreeClient;
    TreeItemIdx                     selectedIdx;
    boolean                         showTree;
    boolean                         treeInitialized;

    QueryBuildRange                 criteriaLocationType;
    QueryBuildRange                 criteriaInventLocation;

    WMSLocationLoadSumCache         wmsLocationLoadSumCache;
}

public void buildTree()
{
    if (showTree)
    {
        wmsLocationTreeClient.buildBaseNode(element.args());
        treeInitialized = true;
        if (treeTab.isExpanded())
        {
            wmsLocationTreeClient.findLocation(wmsLocation);
        }
        formatStr.text(element.formatStr());
    }
}

public void doSelectionChange()
{
    WMSLocation     wmsLocation1;

    if (selectedIdx == 0 || !showTree)
    {
        return;
    }

    wmsLocation1 = wmsLocationTreeClient.wmsLocation(selectedIdx);
    if (wmsLocation1)
    {
        element.args().lookupRecord(wmsLocation1);
        wmsLocation_DS.research(false);
    }
}

public void enableManualUpdateFields()
{
    wmsLocation_ds.object(fieldNum(WMSLocation, wmsLocationId)).allowEdit(wmsLocation.manualName);
    wmsLocation_ds.object(fieldNum(WMSLocation, wmsLocationId)).skip(!wmsLocation.manualName);
}

public void enableManualSortCodeUpdateFields()
{
    wmsLocation_ds.object(fieldNum(WMSLocation, SortCode)).allowEdit(wmsLocation.manualSortCode);
    wmsLocation_ds.object(fieldNum(WMSLocation, SortCode)).skip(!wmsLocation.manualSortCode);
}

public void enableFields()
{
    InventLocation  inventLocation  = wmsLocation.inventLocation();

    this.enableManualUpdateFields();
    this.enableManualSortCodeUpdateFields();

    wmsOnlineCounting.enabled(wmsLocation.RecId);

    if (inventLocation)
    {
        identification_rack.allowEdit(wmsLocation.manualName && inventLocation.wmsRackNameActive);
        identification_rack.skip(!inventLocation.wmsRackNameActive);
        identification_level.allowEdit(wmsLocation.manualName && inventLocation.wmsLevelNameActive);
        identification_level.skip(!inventLocation.wmsLevelNameActive);
        identification_position.allowEdit(wmsLocation.manualName && inventLocation.wmsPositionNameActive);
        identification_position.skip(!inventLocation.wmsPositionNameActive);
    }
    else
    {
        identification_rack.allowEdit(true);
        identification_rack.skip(false);
        identification_level.allowEdit(true);
        identification_level.skip(false);
        identification_position.allowEdit(true);
        identification_position.skip(false);
    }
}

public void findTreeRecord(TreeItemIdx _idx)
{
    selectedIdx = _idx;
    element.doSelectionChange();
}

public str formatStr()
{
    return wmsLocationTreeClient.formatTxt();
}

public void init()
{
    showTree        = true;
    treeInitialized = false;

    if (element.args().parmEnumType())
    {
        parmLocationType = true;
        wmsLocationType = element.args().parmEnum();
    }
    else
    {
        parmLocationType = false;
    }

    wmsLocationLoadSumCache = WMSLocationLoadSumCache::construct();

    super();

    wmsLocationTreeClient = WMSLocationTreeClient::newFromForm(tree, this);

    // subscribe to the treeChanged event
    wmsLocationTreeClient.treeChanged += eventhandler(this.buildTree);

    if (element.args().dataset())
    {
        switch (element.args().dataset())
        {
            case tableNum(InventLocation):
            case tableNum(WMSAisle):
                showTree = true;
                break;
            default:
                treeTab.visible(false);
                showTree = false;
                break;
        }
    }
    if (showTree)
    {
        if (treeTab.isExpanded())
        {
            element.buildTree();
        }
    }
}

protected void refresh()
{
    wmsLocation_DS.research(true);
}

// Display the correct form depending on the Item
public void run()
{
    Args            args = new Args();
    boolean         customForm;

    if (element.args().record())
    {
        switch (element.args().record().TableId)
        {
            case tableNum(InventLocation):
                customForm = InventLocation::find(element.args().record().(fieldNum(InventLocation, InventLocationId))).whsEnabled;
                break;
            case tableNum(WMSLocation):
                customForm = InventLocation::find(element.args().record().(fieldNum(WMSLocation, InventLocationId))).whsEnabled;
                break;
        }

        if (customForm)
        {
            args.record(element.args().record());
            new MenuFunction(menuitemDisplayStr(WHSLocation), MenuItemType::Display).run(args);

            this.closeCancel();
        }
        else
        {
            super();
        }
    }
    else
    {
        super();
    }
}

public void reread()
{
    wmsLocationLoadSumCache.clearCacheForWMSLocation(wmsLocation);
    super();
}

public void delete()
{
    super();
    if (treeTab.isExpanded())
    {
        element.buildTree();
    }
    else
    {
        treeInitialized = false;
    }
}

public void write()
{
    WMSLocation orig = wmsLocation.orig();

    ttsbegin;
    super();

    wmsLocation_DS.setCurrent();

    if (!orig.RecId)
    {
        element.buildTree();
    }

    ttscommit;

    element.enableFields();
}

public void linkActive()
{
    InventItemLocation  inventItemLocation;
    InventDim           inventDim;

    if (element.args() && element.args().dataset() == tableNum(InventItemLocation))
    {
        inventItemLocation = element.args().record();
        inventDim = inventItemLocation.inventDim();
        if (inventDim.InventLocationId)
        {
            criteriaInventLocation.value(inventDim.InventLocationId);
        }
        else
        {
            criteriaInventLocation.value(SysQuery::valueUnlimited());
        }
    }

    super();

    if (element.args() && element.args().dataset() == tableNum(InventDim))
    {
        inventDim = element.args().record();
        if (inventDim.wmsLocationId)
        {
            this.positionToRecord(inventDim.wmsLocation());
        }
    }

    if (showTree && treeInitialized && treeTab.isExpanded())
    {
        element.buildTree();
    }
    else
    {
        treeInitialized = false;
    }
}

public void init()
{
    InventItemLocation  inventItemLocation;
    InventDim           inventDim;
    QueryBuildDataSource    qbds;

    super();

    criteriaLocationType = wmsLocation_ds.query().dataSourceNo(1).addRange(fieldNum(WMSLocation,LocationType));
    criteriaInventLocation = wmsLocation_ds.query().dataSourceNo(1).addRange(fieldNum(WMSLocation,InventLocationId));

    if (element.args() && element.args().dataset() == tableNum(InventItemLocation))
    {
        inventItemLocation = element.args().record();
        inventDim = inventItemLocation.inventDim();
        if (inventDim.InventLocationId)
        {
            criteriaInventLocation.value(inventDim.InventLocationId);
        }
    }

    // Only show Non-WHS locations
    qbds = wmsLocation_ds.query().dataSourceTable(tableNum(WMSLocation));
    WMSLocation::addFilterforWHSEnabled(qbds, NoYes::No);
}

public void create(boolean _append = false)
{
    super(_append);

    generalTab.activatePage();
}

public int active()
{
    int ret;

    element.enableFields();

    ret = super();

    return ret;
}

public void initValue()
{
    super();

    if (parmLocationType)
    {
        wmsLocation.LocationType = wmsLocationType;
    }

    wmsLocation.initWMSLocationId();

    element.enableFields();
}

public void executeQuery()
{
    Object  caller;

    if (parmLocationType)
    {
        criteriaLocationType.value(queryValue(wmsLocationType));
        if (element.args() && element.args().caller())
        {
            caller = element.args().caller();
            criteriaInventLocation.value(queryValue(caller.inventLocationId()));
        }
    }

    //clear the entire cache to get fresh values
    wmsLocationLoadSumCache.clearEntireCache();

    super();
}

