public boolean validate()
{
    boolean ret;

    ret = super();

    if (InventLocation::find(wmsLocation.InventLocationId).whsEnabled)
    {
        ret = checkFailed(strFmt("@WAX5359", wmsLocation.InventLocationId));
    }

    return ret;
}

