public void modified()
{
    super();

    if (wmsLocation.ManualName)
    {
        wmsLocation.wmsLocationId = wmsLocation.orig().wmsLocationId;
        wmsLocation_ds.refresh();
        element.enableFields();
    }
    else
    {
        wmsLocation.wmsLocationId = wmsLocation.makeName();
        wmsLocation_ds.refresh();
        element.enableFields();
    }
}

