public void expand()
{
    super();

    if (showTree)
    {
        if (!treeInitialized)
        {
            element.buildTree();
        }
        else
        {
            wmsLocationTreeClient.findLocation(wmsLocation);
        }
    }
}

public void clicked()
{
    Args args = new Args();

    args.parmObject(wmsLocationTreeClient);

    MenuFunction menuFuntion = new MenuFunction(menuitemdisplaystr(WMSLocationTreeSetup), MenuItemType::Display);
    menuFuntion.run(args);
}

public boolean expanding(int _idx, FormTreeExpand _action, anytype _data)
{
    boolean         ret;

    if (_action == FormTreeExpand::Expand)
    {
        wmsLocationTreeClient.expandNode(_idx);
    }

    ret = super(_idx, _action, _data);

    return ret;
}

public void selectionChanged(FormTreeItem _oldItem, FormTreeItem _newItem, FormTreeSelect _how)
{
    super(_oldItem, _newItem, _how);

    wmsLocationTreeClient.selectionChanged(_oldItem, _newItem, _how);
}
