public class WMSPickingRegistration extends FormRun
{
    Map selectedLines; //key wmsOrderTrans.recId, Value: WMSOrderTrans.orderid;//used to hit best index

    //dimensions setup
    InventDimCtrl_Frm_WMSPickingLinePick  inventDimFormSetup;

    //caller
    FormDataSource          salesTable_ds;
    FormDataSource          inventTransferTable_ds;
    FormDataSource          salesLine_ds;
    FormDataSource          wmsShipment_ds;

    PmfFormCtrl             formCtrl;

}

public void createSelectedLinesMap()
{
    selectedLines = new Map(Types::Int64,Types::Container);
}

//needed for WMSPickingLineCancel
public void doRefresh()
{
    wmsPickingRoute_DS.reread();
    wmsPickingRoute_DS.refresh();
}

//needed for WMSSplit, Unpick and reservation
public void doResearch()
{
    int pos = wmsOrderTrans_DS.getPosition();

    if (PickingRegistrationResearchHeaderToggle::instance().isEnabled())
    {
        int posPickingRoute = wmsPickingRoute_DS.getPosition();
        wmsPickingRoute_DS.research();
        wmsPickingRoute_DS.setPosition(posPickingRoute);
    }
    else
    {
        element.doRefresh();
    }

    wmsOrderTrans_DS.research();
    wmsOrderTrans_DS.setPosition(pos);

    if (salesTable_ds && wmsPickingRoute.ExpeditionStatus == WMSExpeditionStatus::Cancelled)
    {
        salesTable_ds.active();
    }
}

public void enableLineFunctionsButtons()
{
    wmsOrderTransSplit.enabled(wmsOrderTrans.RecId);
    wmsPickingLineCancel.enabled(wmsOrderTrans.canBeCancelled());
    wmsOrderTransUnPick.enabled(wmsOrderTrans.RecId && wmsOrderTrans.canBeUnpicked());
    element.enablePickSelectedButton();
}

public void enablePrintButtons()
{
    wmsPickingList_OrderPickOriginal.enabled(wmsPickingRoute.RecId);
}

public void enableRouteFunctionsButton()
{
    wmsPickingRouteStart.enabled(wmsPickingRoute.ExpeditionStatus == WMSExpeditionStatus::Activated);

    if (wmsPickingRoute.RecId)
    {
        wmsPickingRouteCancel.enabled(wmsPickingRoute.canBeCancelled());
    }
    else
    {
        wmsPickingRouteCancel.enabled(false);
    }
}

public void enableUpdateButtons()
{
    boolean         canUpdate;
    WMSPickingRoute localWMSPickingRoute;

    for (localWMSPickingRoute = wmsPickingRoute_ds.getFirst(true) ? wmsPickingRoute_ds.getFirst(true) : wmsPickingRoute_ds.cursor(); localWMSPickingRoute.RecId; localWMSPickingRoute = wmsPickingRoute_ds.getNext())
    {
        canUpdate = localWMSPickingRoute.canBePickUpdated();

        if (canUpdate)
        {
            break;
        }
    }

    pickAllButton.enabled(canUpdate);
}

public void enablePickSelectedButton()
{
    pickSelectedButton.enabled(wmsPickingRoute.canBePickUpdated() && (selectedLines && selectedLines.elements() != 0));        
}

public boolean hasCaller()
{
    return (salesTable_ds || inventTransferTable_ds || salesLine_ds || wmsShipment_ds);
}

public void init()
{
    formCtrl = PdsFormCtrl_WMSPickingRegistration::newFromForm(element);

    super();

    element.createSelectedLinesMap();

    switch (element.args().dataset())
    {
        case tableNum(SalesTable)           :
            salesTable_ds = FormDataUtil::getFormDataSource(element.args().record());
            break;
        case tableNum(InventTransferTable)  :
            inventTransferTable_ds = FormDataUtil::getFormDataSource(element.args().record());
            break;
        case tableNum(SalesLine)            :
            salesLine_ds = FormDataUtil::getFormDataSource(element.args().record());
            break;
        case tableNum(WMSShipment)          :
            wmsShipment_ds = FormDataUtil::getFormDataSource(element.args().record());
            break;
        default:
            break;
    }

    element.updateDesign(InventDimFormDesignUpdate::Init);

    if (formCtrl)
    {
        formCtrl.setDatasources(wmsOrderTrans, wmsPickingRoute);
        formCtrl.initPost();
    }

    logisticsPostalAddress_ds.addFieldToSelectionList(fieldNum(LogisticsPostalAddress, CountryRegionId));
    logisticsPostalAddressPickingRoute_ds.query().validTimeStateDateTimeRange(DateTimeUtil::minValue(), DateTimeUtil::maxValue());

    if (#PmfEnabled)
    {
        Handling_autoDecreaseQty.helpText("@SYS4110540");
    }
}

public Object inventDimSetupObject()
{
    return inventDimFormSetup;
}

public void pickAllLines()
{
    List list = new List(Types::String);
    WMSPickingRoute localWMSPickingRoute;

    for (localWMSPickingRoute = wmsPickingRoute_ds.getFirst(true) ? wmsPickingRoute_ds.getFirst(true) : wmsPickingRoute_ds.cursor(); localWMSPickingRoute.RecId; localWMSPickingRoute = wmsPickingRoute_ds.getNext())
    {
        list.addEnd(localWMSPickingRoute.PickingRouteID);
    }

    WMSPickingRoute::finishMulti(list.pack());//Note - this is not in a single tts, so some routes might be posted and others fail

    //done to clear all selected

    element.createSelectedLinesMap();
    wmsPickingRoute_DS.research(true); //maybe some are finished and not ready for pick
    wmsPickingRoute_DS.refresh();
    wmsOrderTrans_DS.research();

    element.refreshCallerDatasource();
}

public void pickSelectedLines()
{
    if (selectedLines.elements() == 0)
    {
        throw error("@SYS78876");
    }

    WMSOrderTransType::finishPickingLinesMulti(selectedLines.pack(), '', wmsPickingRoute.OperatorWorker);

    element.createSelectedLinesMap();
    wmsPickingRoute_DS.reread();
    wmsPickingRoute_DS.refresh();
    wmsOrderTrans_DS.research();

    element.refreshCallerDatasource();
}

public void refreshCallerDatasource()
{
    if (wmsShipment_ds)
    {
        wmsShipment_ds.reread();
        wmsShipment_ds.refresh();
    }
}

public void removeLineFromSelectedMap(RecId _wmsOrderTransRecId)
{
    if (selectedLines.exists(_wmsOrderTransRecId))
    {
        selectedLines.remove(_wmsOrderTransRecId);
    }
}

public void updateDesign(InventDimFormDesignUpdate _mode)
{
    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init            :
        case InventDimFormDesignUpdate::LinkActive      :
            if (!inventDimFormSetup)
            {
                inventDimFormSetup  = InventDimCtrl_Frm_WMSPickingLinePick::newFromForm(element);
            }
            // fall through
        case InventDimFormDesignUpdate::Active          :
            inventDimFormSetup.formActiveSetup(InventDimGroupSetup::newItemId(wmsOrderTrans.ItemId), wmsOrderTrans);
            inventDimFormSetup.formSetControls(true);
            break;
        default :
            throw error(strFmt("@SYS54195",funcName()));
    }

    InventTrackingRegisterTrans.enabled(
        WMSOrderTrans.inventTransType == InventTransType::Sales
        && InventTable::find(WMSOrderTrans.itemId).isSalesProcessSerialActive());

    this.allowExternalGiftCardRegistration();
}

public void updateWMSOrderTransDSSettings()
{
    boolean allowEdit;
    allowEdit = wmsOrderTrans.canBeEdited();

    if (selectedLines.exists(wmsOrderTrans.RecId))
    {
        if (allowEdit)
        {
            //recversion could be changed so we need to update map if line is selected
            selectedLines.insert(wmsOrderTrans.RecId,[wmsOrderTrans.OrderId,wmsOrderTrans.RecVersion]);
        }
        else
        {
            selectedLines.remove(wmsOrderTrans.RecId);
        }
    }

    wmsOrderTrans_ds.allowEdit(allowEdit);
}

private void allowExternalGiftCardRegistration()
{
    SalesLine salesLine;
    MCRSalesLine mcrSalesLine;
    SalesTable salesTable;
    RetailSalesTable retailSalesTable;
    RetailStoreTenderTypeTable retailStoreTenderTypeTable;
    RetailStoreTenderTypeCardTable retailStoreTenderTypeCardTable;
    RetailTenderTypeCardTable retailTenderTypeCardTable;

    str transGiftCardItemId = wmsOrderTrans.ItemId;

    if (MCROrderParameters::isCallCenterEnabledAndInUse())
    {
        // Is item an external gift card and physical card type and have empty mcr gift card number
        select firstonly SalesId, InventTransId, RecId from salesLine
            where salesLine.InventTransId == wmsOrderTrans.InventTransId
            join SalesLine, GiftCardType, GiftCardNumber from mcrSalesLine
                where mcrSalesLine.SalesLine == salesLine.RecId
            join SalesId, RecId from salesTable
                where salesTable.SalesId == salesLine.SalesId
            join ChannelReferenceId, SalesTable from retailSalesTable
                where retailSalesTable.SalesTable == salesTable.RecId
            join GiftCardItemId, ConnectorName, Channel, tenderTypeId from retailStoreTenderTypeTable
                where retailStoreTenderTypeTable.GiftCardItemId == transGiftCardItemId
                    && retailStoreTenderTypeTable.ConnectorName != ''
                    && retailStoreTenderTypeTable.Channel == retailSalesTable.RetailChannel
            join tenderTypeId, Channel, cardTypeId from retailStoreTenderTypeCardTable
                where retailStoreTenderTypeCardTable.tenderTypeId == retailStoreTenderTypeTable.tenderTypeId
                    && retailStoreTenderTypeCardTable.Channel == retailSalesTable.RetailChannel
            join cardTypeId, cardTypes from retailTenderTypeCardTable
                where retailTenderTypeCardTable.cardTypeId == retailStoreTenderTypeCardTable.cardTypeId
                    && retailTenderTypeCardTable.cardTypes == RetailCardTypesBase::GiftCard;

        RetailRegisterGiftCardNumber.enabled(mcrSalesLine && mcrSalesLine.GiftCardType == MCRGiftCardType::Physical && mcrSalesLine.GiftCardNumber == '');
        RetailRegisterGiftCardNumber.visible(retailTenderTypeCardTable && retailTenderTypeCardTable.cardTypeId != '');
    }
    else
    {
        RetailRegisterGiftCardNumber.visible(false);
    }
}

public void init()
{
    QueryBuildDataSource queryDataSourceLink;
    QueryBuildLink       qbLink;
    QueryBuildDataSource qbdsWMSPickingRoute;
    QueryBuildRange      qbrWMSPickingRouteType;

    super();

    qbdsWMSPickingRoute = this.query().dataSourceTable(tableNum(WMSPickingRoute));

    qbrWMSPickingRouteType = SysQuery::findOrCreateRange(qbdsWMSPickingRoute,fieldNum(WMSPickingRoute,ShipmentType));
    qbrWMSPickingRouteType.value(enum2str(WMSShipmentType::OrderPick));
    qbrWMSPickingRouteType.status(RangeStatus::Hidden);

    //set up link to caller
    if (element.args().dataset() == tableNum(SalesTable))
    {
        qbdsWMSPickingRoute.clearDynalinks();
        queryDataSourceLink = qbdsWMSPickingRoute.addDataSource(tableNum(WMSPickingRouteLink));
        queryDataSourceLink.relations(true);

        queryDataSourceLink.addDynalink(fieldNum(WMSPickingRouteLink, OrigInventTransRefId),
                                        element.args().record(),
                                        fieldNum(SalesTable, SalesId));
    }
    else if (element.args().dataset() == tableNum(InventTransferTable))
    {
        qbdsWMSPickingRoute.clearDynalinks();
        queryDataSourceLink = qbdsWMSPickingRoute.addDataSource(tableNum(InventTransferTable));

        qbLink = queryDataSourceLink.addLink(fieldNum(WMSPickingRoute, TransRefId),fieldNum(InventTransferTable, TransferId));
    }
    else if (element.args().dataset() == tableNum(SalesLine))
    {
        qbdsWMSPickingRoute.clearDynalinks();

        queryDataSourceLink = qbdsWMSPickingRoute.addDataSource(tableNum(WMSPickingRouteLink));
        queryDataSourceLink.relations(true);
        queryDataSourceLink.addDynalink(fieldNum(WMSPickingRouteLink, OrigInventTransRefId),
                                        element.args().record(),
                                        fieldNum(SalesLine, SalesId));

        if (element.args().caller()
            && element.args().caller().name() == formStr(ProjSalesItemReq)
            && WMSPickingRegistrationQueryBySalesLineItemIdFlight::instance().isEnabled())
        {
            QueryBuildDataSource queryDatasourceWMSOrderTrans = this.query().dataSourceTable(tableNum(WMSOrderTrans));
            if (!queryDatasourceWMSOrderTrans)
            {
                queryDatasourceWMSOrderTrans = qbdsWMSPickingRoute.addDataSource(tableNum(WMSOrderTrans));
            }
            queryDatasourceWMSOrderTrans.relations(true);
            queryDatasourceWMSOrderTrans.joinMode(JoinMode::ExistsJoin);
            queryDatasourceWMSOrderTrans.addDynalink(fieldNum(WMSOrderTrans, itemId), element.args().record(), fieldNum(SalesLine, ItemId));
        }
    }
}

