public void linkActive()
{
    SalesTable          localSalesTable;
    InventTransferTable localInventTransferTable;
    SalesLine           localSalesLine;

    if (salesTable_ds)
    {
        this.query().dataSourceTable(tableNum(WMSPickingRouteLink)).clearDynalinks();
        this.query().dataSourceTable(tableNum(WMSPickingRouteLink)).clearRanges();

        for (localSalesTable = salesTable_ds.getFirst(true) ? salesTable_ds.getFirst(true) : salesTable_ds.cursor(); localSalesTable; localSalesTable = salesTable_ds.getNext())
        {
            this.query().dataSourceTable(tableNum(WMSPickingRouteLink)).addRange(fieldNum(WMSPickingRouteLink, OrigInventTransRefId)).value(queryValue(localSalesTable.SalesId));
        }
    }
    else if (inventTransferTable_ds)
    {
        this.query().dataSourceTable(tableNum(InventTransferTable)).clearDynalinks();
        this.query().dataSourceTable(tableNum(InventTransferTable)).clearRanges();

        for (localInventTransferTable = inventTransferTable_ds.getFirst(true) ? inventTransferTable_ds.getFirst(true) : inventTransferTable_ds.cursor(); localInventTransferTable; localInventTransferTable = inventTransferTable_ds.getNext())
        {
            this.query().dataSourceTable(tableNum(InventTransferTable)).addRange(fieldNum(InventTransferTable, TransferId)).value(queryValue(localInventTransferTable.TransferId));
        }
    }
    else if (salesLine_ds)
    {
        this.query().dataSourceTable(tableNum(WMSPickingRouteLink)).clearDynalinks();
        this.query().dataSourceTable(tableNum(WMSPickingRouteLink)).clearRanges();
        for (localSalesLine = salesLine_ds.getFirst(true) ? salesLine_ds.getFirst(true) : salesLine_ds.cursor(); localSalesLine; localSalesLine = salesLine_ds.getNext())
        {
            this.query().dataSourceTable(tableNum(WMSPickingRouteLink)).addRange(fieldNum(WMSPickingRouteLink, OrigInventTransRefId)).value(queryValue(localSalesLine.SalesId));
        }
    }

    super();
}

