public void write()
{
    boolean     research;
    InventDim   inventDim1;
    InventDimId inventDimIdOrig = wmsOrderTrans.InventDimId;
    InventDimId inventDimIdNew  = wmsOrderTrans.InventDimId;

    try
    {
        ttsbegin;

        if (formCtrl)
        {
            formCtrl.dataSourceWritePre();
        }

        inventDim1 = InventDim::findOrCreate(inventDim);
        inventDimIdNew = inventDim1.InventDimId;

        using (WMSOrderRequestQtyUpdateWhenUpdPickQtyContext context = WMSOrderRequestQtyUpdateWhenUpdPickQtyContext::construct())
        {
            context.parmNeedUpdateWMSOrderRequestQty(WMSOrderTrans.inventTable().whsItemIsWHSControlled());

            research = wmsOrderTrans.wmsOrderTransType().updateQty();
        }

        if (inventDimIdOrig != inventDimIdNew)
        {
            boolean lockActiveDimensions = this.mustLockActiveDimensions();

            if (!lockActiveDimensions)
            {
                WMSPickingRoute pickingRoute = wmsOrderTrans.wmsPickingRoute();
                lockActiveDimensions = pickingRoute && this.mustLockActiveDimensionsForPickingRoute(pickingRoute);
            }

            wmsOrderTrans.InventDimId = inventDimIdOrig;
            using (InventUpdNeedSwitchDimForWHSItemContext context = InventUpdNeedSwitchDimForWHSItemContext::construct())
            {
                context.parmNeedCheckSwitchDimForWHSItemFromPicking(WMSOrderTrans.inventTable().whsItemIsWHSControlled());

                wmsOrderTrans.checkFinishPickingLine(inventDim1, wmsOrderTrans.Qty, wmsOrderTrans.PdsCWQty, lockActiveDimensions);
            }

            WMSOrderTrans wmsOrderTransLocal;
            select firstonly RecVersion from wmsOrderTransLocal where wmsOrderTransLocal.RecId == wmsOrderTrans.RecId;

            if (wmsOrderTrans.RecVersion != wmsOrderTransLocal.RecVersion)
            {
                wmsOrderTrans.reread();
            }

            wmsOrderTrans.InventDimId = inventDimIdNew;
            wmsOrderTrans.changeToInventDimFromInventDim();
        }

        super();

        //recversion changed so we need to update map if line is selected
        if (selectedLines.exists(wmsOrderTrans.RecId))
        {
            selectedLines.insert(wmsOrderTrans.RecId,[wmsOrderTrans.OrderId,wmsOrderTrans.RecVersion]);
        }

        if (inventDimIdOrig != inventDimIdNew && PdsGlobal::pdsIsCWItem(wmsOrderTrans.ItemId))
        {
            wmsOrderTrans.splitByAllInventTrans(true);
            research = true;
        }

        ttscommit;

        if (WMSPickingRegistrationReservationSplitRefreshFlight::instance().isEnabled())
        {
            if (wmsOrderTrans.InventDimId != inventDim.InventDimId)
            {
                inventDim.data(InventDim::find(wmsOrderTrans.InventDimId));
                inventDim_ds.setCurrent();
                research = true;
            }

            if (research)
            {
                wmsOrderTrans_ds.research(research);
            }
        }
        else
        {
            if (research)
            {
                wmsOrderTrans_ds.research();
            }
            else if (wmsOrderTrans.InventDimId != inventDim.InventDimId)
            {
                inventDim.data(InventDim::find(wmsOrderTrans.InventDimId));
                inventDim_ds.setCurrent();
            }
        }
    }
    catch(Exception::Error)
    {
        wmsOrderTrans_ds.reread();//buffer have had inventdim changed so this needs to be reverted
        wmsOrderTrans_ds.refresh();
    }
}

/// <summary>
/// Determines whether active dimensions must be locked.
/// </summary>
/// <returns>true, if active dimensions must be locked; otherwise, false.</returns>
protected boolean mustLockActiveDimensions()
{
     return wmsOrderTrans.ExpeditionStatus    == WMSExpeditionStatus::Started
            || wmsOrderTrans.ExpeditionStatus == WMSExpeditionStatus::Picked
            || wmsOrderTrans.ExpeditionStatus == WMSExpeditionStatus::Staged
            || wmsOrderTrans.ExpeditionStatus == WMSExpeditionStatus::Loaded
            || wmsOrderTrans.ExpeditionStatus == WMSExpeditionStatus::Complete
            || wmsOrderTrans.ExpeditionStatus == WMSExpeditionStatus::Cancelled;
}

/// <summary>
/// Determines whether active dimensions must be locked for picking route.
/// </summary>
/// <param name = "_pickingRoute">Picking route information.</param>
/// <returns>true, if active dimensions must be locked for picking route; otherwise, false.</returns>
protected boolean mustLockActiveDimensionsForPickingRoute(WMSPickingRoute _pickingRoute)
{
    return _pickingRoute.ExpeditionStatus    == WMSExpeditionStatus::Started
           || _pickingRoute.ExpeditionStatus == WMSExpeditionStatus::Picked
           || _pickingRoute.ExpeditionStatus == WMSExpeditionStatus::Staged
           || _pickingRoute.ExpeditionStatus == WMSExpeditionStatus::Loaded
           || _pickingRoute.ExpeditionStatus == WMSExpeditionStatus::Complete
           || _pickingRoute.ExpeditionStatus == WMSExpeditionStatus::Cancelled;
}

public edit boolean markForPick(
    boolean             _set,
    WMSOrderTrans       _wmsOrderTrans,
    boolean             _mark)
{
    if (_set)
    {
        if (!_mark)
        {
            if (selectedLines.exists(_wmsOrderTrans.RecId))
            {
                selectedLines.remove(_wmsOrderTrans.RecId);
            }
        }
        else
        {
            selectedLines.insert(_wmsOrderTrans.RecId,[_wmsOrderTrans.OrderId,_wmsOrderTrans.RecVersion]);
        }
    }

    return selectedLines.exists(_wmsOrderTrans.RecId);
}

public edit InventQty  qty(
    boolean             _set,
    WMSOrderTrans       _wmsOrderTrans,
    InventQty           _qty)
{
    UnitOfMeasureDecimalPrecision   decimalPrecision;

    if (_set)
    {
        decimalPrecision = UnitOfMeasure::findBySymbol(_wmsOrderTrans.unitId()).DecimalPrecision;
        if (_qty != decRound(_qty, decimalPrecision))
            throw error(strFmt("@SYS83631", decimalPrecision));

        _wmsOrderTrans.Qty = _qty;

        if (formCtrl)
        {
            formCtrl.fieldModified(fieldNum(WMSOrderTrans, Qty));
            wmsOrderTrans_ds.refresh();
        }
    }

    return _wmsOrderTrans.Qty;
}

public edit PdsCWInventQty pdsCWQty(
    boolean         _set,
    WMSOrderTrans   _wmsOrderTrans,
    PdsCWInventQty  _pdsCWQty)
{
    if (_set)
    {
        _wmsOrderTrans.PdsCWQty = _pdsCWQty;
        _wmsOrderTrans.Qty = 0;

        if (formCtrl)
        {
            formCtrl.fieldModified(fieldNum(WMSOrderTrans, PdsCWQty));
            wmsOrderTrans_ds.refresh();
        }
    }

    return _wmsOrderTrans.PdsCWQty ;
}

