public boolean validate()
{
    boolean     ret;
    SalesLine   salesLine;

    boolean validatePick()
    {
        PdsBatchDispCheck_PickList  pdsCheckPick;

        pdsCheckPick = PdsBatchDispCheck_PickList::construct();

        return pdsCheckPick.validateCheck(
                wmsOrderTrans,
                inventDim.InventBatchId);
    }

    ret = super();

    if (ret
        && #PdsShelfEnabled
        && wmsOrderTrans.inventTable().isItemBatchActivated())
    {
        switch (wmsOrderTrans.InventTransType)
        {
            case InventTransType::Sales:
                salesLine = SalesLine::findInventTransId(wmsOrderTrans.InventTransId);
                ret = InventBatch::pdsCheckExpiryDate(
                        inventdim.InventBatchId,
                        wmsOrderTrans.ItemId,
                        salesLine.ReceiptDateRequested,
                        true,
                        wmsOrderTrans.Customer,
                        InventParameters::find().PdsOverrideFEFO,
                        inventDim)
                    && validatePick();
                break;
            case InventTransType::InventTransfer:
                ret = InventBatch::pdsCheckExpiryDate(
                        inventdim.InventBatchId,
                        wmsOrderTrans.ItemId,
                        wmsOrderTrans.DlvDate,
                        true,
                        '',
                        InventParameters::find().PdsOverrideFEFO)
                    && validatePick();
                break;
            case InventTransType::WMSOrder:
                ret = InventBatch::pdsCheckExpiryDate(
                        inventdim.InventBatchId,
                        wmsOrderTrans.ItemId,
                        wmsOrderTrans.DlvDate,
                        true,
                        '',
                        InventParameters::find().PdsOverrideFEFO)
                    && validatePick();
                break;
        }
    }

    return ret;
}

