public void clicked()
{
    buttonLineInventReservation.enabled(wmsOrderTrans.ExpeditionStatus != WMSExpeditionStatus::Complete && wmsOrderTrans.ExpeditionStatus != WMSExpeditionStatus::Cancelled && wmsOrderTrans.OrderType == WMSOrderType::OrderPick);
    super();
}

public void clicked()
{
    element.enableLineFunctionsButtons();
    super();
}

public void clicked()
{
    super();

    element.refreshCallerDatasource();
}
