[Form] public class WrkCtrBulkResReqEditWizard extends FormRun { WrkCtrBulkResReqEditWizard sysWizard;
#define.WelcomeTab(1) #define.SearchCriteria(2) #define.ActionTab(3) #define.NewRequirementTab(4) #define.SummaryTab(5)
#define.Route('Route') #define.ProdRoute('ProdRoute') #define.PC('PC') #define.HourForecast('HourForecast') }

public void checkCaller()
{
    WrkCtrBulkResReqEditWizard wizard = this.wizard();

    if (wizard)
    {
        switch (wizard.parmCallerDest())
        {
            case #Route:
                routesCheckBox.value(1);
                break;
            case #ProdRoute:
                prodRoutesCheckBox.value(1);
                break;
            case #PC:
                prodConfigRoutesCheckBox.value(1);
                break;
            case #HourForecast:
                hourForecastCheckBox.value(1);
                searchByOperation.visible(false);
                break;
        }
    }
}

public void destinationChanged()
{
    boolean nextEnabled = routesCheckBox.checked()
        || prodRoutesCheckBox.checked()
        || prodConfigRoutesCheckBox.checked()
        || hourForecastCheckBox.checked();

    this.wizard().nextEnabled(nextEnabled, #ActionTab, false);
}

void init()
{
    super();
    if (element.Args().caller())
    {
        sysWizard = element.Args().caller();
    }
    else
    {
        WrkCtrBulkResReqEditWizard::main(new Args());
        element.closeCancel();
    }

    this.checkCaller();
}

public void setSearchCriteriaNavigation()
{
    boolean nextEnabled = true;
    boolean gridIsValid = false;

    // at least one group should be enabled
    nextEnabled = searchByResourceRequirements.optionValue() || searchByOperation.optionValue();

    if (searchByOperation.optionValue())
    {
        nextEnabled = nextEnabled && operIdStringEdit.text();
    }

    if (searchByResourceRequirements.optionValue())
    {
        nextEnabled = nextEnabled && wrkCtrRequirementWizardFilterTmp.RecId;
    }

    this.wizard().nextEnabled(nextEnabled, #SearchCriteria, false);
}

