public void dataChanged()
{
    boolean skip = false;
    WrkCtrBulkResReqEditWizard wizard = element.wizard();

    super();

    switch (wrkCtrRequirementWizardSearchCriteriaTmp.ActionType)
    {
        case WrkCtrBulkResReqActionType::DeleteMatchingSearchCriteria:
        case WrkCtrBulkResReqActionType::DeleteAll:
            skip = true;
            break;
    }

    wizard.skipTab(#NewRequirementTab, skip);
}

