display Description requirementDescription(WrkCtrRequirementWizardNewReqTmp _wrkCtrRequirementWizardNewReqTmp)
{
    Common activeSubtype = _wrkCtrRequirementWizardNewReqTmp.activeSubtype();
    if (activeSubtype != null)
    {
        return activeSubtype.requirementDescription();
    }

    return '';
}

public boolean leave()
{
    boolean ret;

    ret = super();

    if (ret)
    {
        this.enableNext();
    }
    return ret;
}

public void changeDisplay()
{
    groupCapabilityRequirement.visible(wrkCtrRequirementWizardNewReqTmp.RelationshipType == WrkCtrActivityRequirementType::Capability);
    groupSkillRequirement.visible(wrkCtrRequirementWizardNewReqTmp.RelationshipType == WrkCtrActivityRequirementType::Skill);

    wrkCtrRequirementWizardNewReqTmp_ds.object(fieldNum(WrkCtrRequirementWizardNewReqTmp, UsedForOperationScheduling)).allowEdit(wrkCtrRequirementWizardNewReqTmp.getActivityRequirement().allowEditSchedulingFlags());
    wrkCtrRequirementWizardNewReqTmp_ds.object(fieldNum(WrkCtrRequirementWizardNewReqTmp, UsedForJobScheduling)).allowEdit(wrkCtrRequirementWizardNewReqTmp.getActivityRequirement().allowEditSchedulingFlags());

    this.enableNext();
}

public void enableNext()
{
    element.wizard().nextEnabled(wrkCtrRequirementWizardNewReqTmp.RecId, #NewRequirementTab, false);
}

public int active()
{
    int ret;

    ret = super();

    if (ret)
    {
        this.changeDisplay();
    }

    return ret;
}

edit WrkCtrActivityRequirementEdit requirementEdit(
    boolean                                 _set,
    WrkCtrRequirementWizardNewReqTmp        _requirementWizardNewReqTmp,
    WrkCtrActivityRequirementEdit           _wrkCtrActivityRequirementEdit
    )
{
    WrkCtrActivityRequirementEdit   wrkCtrActivityRequirementEdit = _requirementWizardNewReqTmp.ActivityRequirementEdit;

    if (_set)
    {
        WrkCtrActivityRequirement wrkCtrActivityRequirement = _requirementWizardNewReqTmp.getActivityRequirement();
        Common activeSubtype = _requirementWizardNewReqTmp.activeSubtype();

        wrkCtrActivityRequirementEdit = wrkCtrActivityRequirement.requirementEdit(
                                                     _set,
                                                     false,
                                                     activeSubtype,
                                                     _wrkCtrActivityRequirementEdit,
                                                     curext());

        _requirementWizardNewReqTmp.ActivityRequirementEdit = wrkCtrActivityRequirementEdit;
        _requirementWizardNewReqTmp.updateFromSubtype(activeSubtype);
        _requirementWizardNewReqTmp.write();

        this.enableNext();
    }

    return wrkCtrActivityRequirementEdit;
}

