}

private void displayDetails()
{
    WrkCtrBulkResReqEditWizard wizard = element.wizard();

    stringSummaryWhereDetails.text(wizard.whereDetails(
            searchByOperation.optionValue()?  operIdStringEdit.text() : ''));

    stringSummaryActionDetails.label(wizard.actionLabel());
    stringSummaryActionDetails.text(wizard.actionDetails());
    stringSummaryDestinationDetails.text(wizard.destinationDetails());
    stringSummaryNumberDetails.text(int2str(wizard.numberOfAffectedActivities()));
}

private void readAction()
{
    WrkCtrResReqBulkEditAction action;
    WrkCtrResReqBulkEditNewAction addAction;
    WrkCtrRequirementWizardNewReqTmp template;
    WrkCtrBulkResReqEditWizard wizard = element.wizard();

    switch (wrkCtrRequirementWizardSearchCriteriaTmp.ActionType)
    {
        case WrkCtrBulkResReqActionType::Add:
            action = new WrkCtrResReqBulkEditAddAction();
            break;
        case WrkCtrBulkResReqActionType::ReplaceMatchingSearchCriteria:
            action = new WrkCtrResReqBulkEditReplaceAction();
            break;
        case WrkCtrBulkResReqActionType::ReplaceAll:
            action = new WrkCtrResReqBulkEditReplaceAllAction();
            break;
        case WrkCtrBulkResReqActionType::DeleteMatchingSearchCriteria:
            action = new WrkCtrResReqBulkEditDeleteAction();
            break;
        case WrkCtrBulkResReqActionType::DeleteAll:
            action = new WrkCtrResReqBulkEditDeleteAllAction();
            break;
    }

    addAction = action as WrkCtrResReqBulkEditNewAction;
    if (addAction)
    {
        wrkCtrRequirementWizardNewReqTmp wrkCtrRequirementWizardNewReqTmpLocal;
        wrkCtrRequirementWizardNewReqTmpLocal.setTmpData(wrkCtrRequirementWizardNewReqTmp);

        while select wrkCtrRequirementWizardNewReqTmpLocal
        {
            template.data(wrkCtrRequirementWizardNewReqTmpLocal);
            addAction.addNewRequirementTemplate(template);
        }
    }
    wizard.parmAction(action);
}

private void readDestinations()
{
    WrkCtrBulkResReqEditWizard wizard = element.wizard();
    str oprIdFilter;

    if (searchByOperation.optionValue())
    {
        oprIdFilter = operIdStringEdit.text();
    }

    wizard.clearDestinations();

    if (routesCheckBox.checked())
    {
        wizard.addDestination(new WrkCtrResReqBulkEditRoutesDestination(oprIdFilter));
    }

    if (prodRoutesCheckBox.checked())
    {
        wizard.addDestination(new WrkCtrResReqBulkEditProdRouteDestination(oprIdFilter));
    }

    if (prodConfigRoutesCheckBox.checked())
    {
        wizard.addDestination(new WrkCtrResReqBulkEditPCRoutesDestination(oprIdFilter));
    }

    if (hourForecastCheckBox.checked())
    {
        wizard.addDestination(new WrkCtrResReqBulkEditHourForecastDest());
    }
}

private void readSearchFilters()
{
    WrkCtrResReqBulkEditExactFilter exactFilter;
    WrkCtrResReqBulkEditPartialFilter partialFilter;
    WrkCtrRequirementWizardFilterTmp filterTmp;
    WrkCtrBulkResReqEditWizard wizard = element.wizard();

    if (searchByResourceRequirements.optionValue())
    {
        wrkCtrRequirementWizardFilterTmp wrkCtrRequirementWizardFilterTmpLocal;
        wrkCtrRequirementWizardFilterTmpLocal.setTmpData(wrkCtrRequirementWizardFilterTmp);

        if (wrkCtrRequirementWizardSearchCriteriaTmp.ExactMatch == WrkCtrBulkResReqSearchType::Exact)
        {
            exactFilter = new WrkCtrResReqBulkEditExactFilter();
            while select wrkCtrRequirementWizardFilterTmpLocal
            {
                filterTmp.data(wrkCtrRequirementWizardFilterTmpLocal);
                exactFilter.addFilteringItem(filterTmp);
            }

            wizard.parmFilter(exactFilter);
        }
        else
        {
            partialFilter = new WrkCtrResReqBulkEditPartialFilter();
            while select wrkCtrRequirementWizardFilterTmpLocal
            {
                filterTmp.data(wrkCtrRequirementWizardFilterTmpLocal);
                partialFilter.addFilteringItem(filterTmp);
            }

            wizard.parmFilter(partialFilter);
        }
    }
    else
    {
        wizard.parmFilter(new WrkCtrResReqBulkEditPartialFilter());
    }
}

public void tabChanged(int _fromTab, int _toTab)
{
    switch (_toTab)
    {
        case #SearchCriteria:
            element.setSearchCriteriaNavigation();
            break;

        case #ActionTab:
            element.destinationChanged();
            break;

        case #SummaryTab:
            this.readSearchFilters();
            this.readAction();
            this.readDestinations();

            this.displayDetails();
            break;
    }

    super(_fromTab, _toTab);
}

