public void lookup()
{
    SysTableLookup lookup = SysTableLookup::newParameters(tableNum(RouteOprTable), operIdStringEdit);
    Query                   query               = new Query();
    QueryBuildDataSource    queryBuildDataSource;

    lookup.addLookupfield(fieldNum(RouteOprTable, OprId),true);
    lookup.addLookupfield(fieldNum(RouteOprTable, Name));

    queryBuildDataSource = query.addDataSource(tableNum(RouteOprTable));

    lookup.parmQuery(query);
    lookup.performFormLookup();
}

