    [Form]
    public class WrkCtrGroupDateCalendar extends FormRun
    {
        TransDate           fromDate;
        NoYes               showDayName;

        WrkCtrCapacitySum   wrkCtrCapacitySum;

        #define.NumOfDays(7)
