/// <summary>
/// Edits the calendar id of the resource (both individual and group).
/// </summary>
/// <param name="_set">
/// A Boolean that indicates whether to set the calendar.
/// </param>
/// <param name="_wrkCtrTable">
/// The <c>WrkCtrTable</c> record to set the calendar for.
/// </param>
/// <param name="_calendarId">
/// The ID of the calendar.
/// </param>
/// <param name="_dayNum">
/// The day number offset where 1 if the first day shown in the form.
/// </param>
/// <returns>
/// The ID of the resource's calendar.
/// </returns>
public CalendarId calendarId(boolean _set, WrkCtrTable _wrkCtrTable, CalendarId _calendarId, int _dayNum)
{
    CalendarId calendarId = _wrkCtrTable.editCalendar(_set, fromDate + _dayNum - 1, _calendarId);

    if (_set)
    {
        // Force the cache to be refreshed
        wrkCtrCapacitySum = null;
    }

    return calendarId;
}

/// <summary>
/// Retrieves the capacity of the individual resource or resource group.
/// </summary>
/// <param name="_wrkCtrTable">
/// The <c>WrkCtrTable</c> record to set the calendar for.
/// </param>
/// <param name="_dayNum">
/// The day number offset where 1 if the first day shown in the form.
/// </param>
/// <returns>
/// The capacity of the resource.
/// </returns>
public CapacityHours capacity(WrkCtrTable _wrkCtrTable, int _dayNum)
{
    if (!wrkCtrCapacitySum)
    {
        wrkCtrCapacitySum = new WrkCtrCapacitySum(true,true);
    }

    wrkCtrCapacitySum.find(_wrkCtrTable.WrkCtrId, fromDate + _dayNum - 1,false);
    return wrkCtrCapacitySum.rec_Capacity() / 3600;
}

/// <summary>
/// Validates that the calendar id given in a <c>FormStringControl</c> control is valid.
/// </summary>
/// <param name="_ret">
/// A Boolean value indicating the result of the primary control validation.
/// </param>
/// <param name="_ctrl">
/// The control to validate.
/// </param>
/// <returns>
/// true if the control contains a valid calendar ID; otherwise, false.
/// </returns>
public boolean checkCalendarId(boolean _ret, FormStringControl _ctrl)
{
    if (_ctrl.text() && ! WorkCalendarTable::exist(_ctrl.text()))
    {
        _ret = checkFailed(strfmt(WorkCalendarTable::txtNotExist(),_ctrl.text()));
    }

    return _ret;
}

/// <summary>
/// Edit method used for the FromDate control.
/// </summary>
/// <param name="_set">
/// A Boolean that indicates whether to set the date.
/// </param>
/// <param name="_transDate">
/// The date to set.
/// </param>
/// <returns>
/// The current used date.
/// </returns>
edit WrkCtrFromDate editFromDate(boolean _set, TransDate _transDate)
{
    if (_set)
    {
        element.setFromDate(_transDate);
    }

    return fromDate;
}

void init()
{
    super();

    radioButtonViewHeadings.add("@SYS57639");
    radioButtonViewHeadings.add("@SYS30081");

    radioButtonViewMode.add("@SYS11839");
    radioButtonViewMode.add("@SYS8345");

    wrkCtrCapacitySum = new WrkCtrCapacitySum(true,true);

    showDayName = NoYes::Yes;

    element.setViewMode(true);
    element.setFromDate(DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()));
}

/// <summary>
/// Sets the internal fromDate variable and changes column headers to reflect the selection.
/// </summary>
/// <param name="_fromDate">
/// The new base date to ise in the form.
/// </param>
void setFromDate(TransDate _fromDate)
{
    fromDate = _fromDate;

    calendarIdGroup1.label(showDayName ? dayname(dayofwk(fromDate)): date2StrUsr(fromDate,       DateFlags::FormatAll));
    calendarIdGroup2.label(showDayName ? dayname(dayofwk(fromDate+1)):date2StrUsr(fromDate+1,    DateFlags::FormatAll));
    calendarIdGroup3.label(showDayName ? dayname(dayofwk(fromDate+2)):date2StrUsr(fromDate+2,    DateFlags::FormatAll));
    calendarIdGroup4.label(showDayName ? dayname(dayofwk(fromDate+3)):date2StrUsr(fromDate+3,    DateFlags::FormatAll));
    calendarIdGroup5.label(showDayName ? dayname(dayofwk(fromDate+4)):date2StrUsr(fromDate+4,    DateFlags::FormatAll));
    calendarIdGroup6.label(showDayName ? dayname(dayofwk(fromDate+5)):date2StrUsr(fromDate+5,    DateFlags::FormatAll));
    calendarIdGroup7.label(showDayName ? dayname(dayofwk(fromDate+6)):date2StrUsr(fromDate+6,    DateFlags::FormatAll));

    calendarIdResource1.label(calendarIdGroup1.label());
    calendarIdResource2.label(calendarIdGroup2.label());
    calendarIdResource3.label(calendarIdGroup3.label());
    calendarIdResource4.label(calendarIdGroup4.label());
    calendarIdResource5.label(calendarIdGroup5.label());
    calendarIdResource6.label(calendarIdGroup6.label());
    calendarIdResource7.label(calendarIdGroup7.label());

    capacityGroup1.label(calendarIdGroup1.label());
    capacityGroup2.label(calendarIdGroup2.label());
    capacityGroup3.label(calendarIdGroup3.label());
    capacityGroup4.label(calendarIdGroup4.label());
    capacityGroup5.label(calendarIdGroup5.label());
    capacityGroup6.label(calendarIdGroup6.label());
    capacityGroup7.label(calendarIdGroup7.label());

    capacityResource1.label(calendarIdGroup1.label());
    capacityResource2.label(calendarIdGroup2.label());
    capacityResource3.label(calendarIdGroup3.label());
    capacityResource4.label(calendarIdGroup4.label());
    capacityResource5.label(calendarIdGroup5.label());
    capacityResource6.label(calendarIdGroup6.label());
    capacityResource7.label(calendarIdGroup7.label());

    fromDateCtrl.update();
}

