public void executeQuery()
{
    // Limit resource group membership to only those active in the period
    this.query().validTimeStateDateRange(fromDate, fromDate + #NumOfDays - 1);

    super();
}

public void init()
{
    QueryBuildDataSource qbdsWrkCtrTableResource;
    QueryBuildDataSource qbdsWrkCtrResourceGroupResource;

    super();

    // Limit the individual resources to only include those which belong to the resource group
    // at some point in the time interval.
    qbdsWrkCtrTableResource = this.query().dataSourceName(wrkCtrTableResource_ds.name());

    qbdsWrkCtrTableResource.clearLinks();
    qbdsWrkCtrTableResource.clearDynalinks();

    qbdsWrkCtrResourceGroupResource = qbdsWrkCtrTableResource.addDataSource(tablenum(WrkCtrResourceGroupResource));
    qbdsWrkCtrResourceGroupResource.joinMode(JoinMode::ExistsJoin);
    qbdsWrkCtrResourceGroupResource.addLink(fieldnum(WrkCtrTable, WrkCtrId),fieldnum(WrkCtrResourceGroupResource, WrkCtrId), wrkCtrTableResource_ds.name());
    qbdsWrkCtrResourceGroupResource.addDynalink(fieldnum(WrkCtrResourceGroupResource, ResourceGroup), wrkCtrResourceGroup, fieldnum(WrkCtrResourceGroup, RecId));
}

