public class WrkCtrResourceAbilityMap extends FormRun
{
    WrkCtrResourceAbilityMapController      formController;
    boolean                                 orientationSwitched;

}

public str abilityId()
{
    FormTableControl    tableControl = element.activeTableControl();

    if (!orientationSwitched)
    {
        return tableControl.colLabel(tableControl.column());
    }
    else
    {
        return tableControl.rowLabel(tableControl.row());
    }
}

public FormTableControl activeTableControl()
{
    switch (true)
    {
        case capabilityTabPage.isActivePage():
            return capabilityTable;
        case skillTabPage.isActivePage():
            return skillTable;
        case courseTabPage.isActivePage():
            return courseTable;
        case certificateTabPage.isActivePage():
            return certificateTable;
        case titleTabPage.isActivePage():
            return titleTable;
    }

    return capabilityTable;
}

public edit TransDate dateFilter(boolean _set, TransDate _dateFilter)
{
    if (_set && _dateFilter != formController.parmDateFilter())
    {
        formController.parmDateFilter(_dateFilter);
        this.loadData();
    }

    return formController.parmDateFilter();
}

public void enableActionPaneButtons()
{
    FormTableControl tableControl = this.activeTableControl();
    boolean          isWorker = (WrkCtrTable::find(this.resourceId()).Worker != 0);

    buttonViewResource.enabled(tableControl.columns() > 0 && tableControl.row() > 0 && tableControl.column() > 0);

    buttonViewCapability.enabled(buttonViewResource.enabled() && capabilityTabPage.isActivePage());
    buttonViewSkill.enabled(buttonViewResource.enabled() && skillTabPage.isActivePage() && isWorker);
    buttonViewCourse.enabled(buttonViewResource.enabled() && courseTabPage.isActivePage() && isWorker);
    buttonViewCertificate.enabled(buttonViewResource.enabled() && certificateTabPage.isActivePage() && isWorker);
    buttonViewTitle.enabled(buttonViewResource.enabled() && titleTabPage.isActivePage() && isWorker);
}

private void fillTable(FormTableControl _table, WrkCtrActivityRequirementType _abilityType)
{
    SetEnumerator   rowEnumerator;
    SetEnumerator   columnEnumerator;
    MapEnumerator   mapEnumerator;

    Map             cellData;
    Set             columnHeaders;
    Set             rowHeaders;

    Map             rowNumbers      = new Map(Types::String, Types::Integer);
    Map             columnNumbers   = new Map(Types::String, Types::Integer);

    WrkCtrAbility   wrkCtrAbility;
    int             row;
    int             column;
    str             rowStr;
    str             columnStr;

    if (!formController.abilityDataChanged(_abilityType))
    {
        // Nothing has changed so no need to refresh
        return;
    }

    cellData = formController.abilityData(_abilityType);

    if (!orientationSwitched)
    {
        columnHeaders = formController.abilityColumnHeaders(_abilityType);
        rowHeaders = formController.rowHeaders();
    }
    else
    {
        rowHeaders = formController.abilityColumnHeaders(_abilityType);
        columnHeaders = formController.rowHeaders();
    }

    // Set new row and column headers
    _table.clear();
    _table.rows(rowHeaders.elements());
    _table.columns(columnHeaders.elements());

    rowEnumerator = rowHeaders.getEnumerator();
    while (rowEnumerator.moveNext())
    {
        rowStr = rowEnumerator.current();
        row++;
        _table.setRowLabel(row, rowStr);
        rowNumbers.insert(rowStr,row);
    }

    columnEnumerator = columnHeaders.getEnumerator();
    while (columnEnumerator.moveNext())
    {
        column++;
        columnStr = columnEnumerator.current();
        _table.setColLabel(column, columnStr);
        columnNumbers.insert(columnStr,column);
    }

    // Insert cell data
    mapEnumerator = cellData.getEnumerator();
    while (mapEnumerator.moveNext())
    {
        if (!orientationSwitched)
        {
            [columnStr,rowStr] = mapEnumerator.currentKey();
        }
        else
        {
            [rowStr,columnStr] = mapEnumerator.currentKey();
        }

        wrkCtrAbility = mapEnumerator.currentValue();

        column = columnNumbers.lookup(columnStr);
        row = rowNumbers.lookup(rowStr);

        if (wrkCtrAbility.LevelName != '')
        {
            _table.cell(column,row).data(wrkCtrAbility.LevelName);
        }
        else if (wrkCtrAbility.LevelValue > 0)
        {
            _table.cell(column,row).data(num2str(wrkCtrAbility.LevelValue,-1,-1,-1,-1));
        }
        else
        {
            // Show a unicode 25CF "dot" to indicate that an entry exists
            _table.cell(column,row).data(num2char(9679));
        }
    }

    // Mark table data as up to date
    formController.setAbilityDataChanged(_abilityType, false);

    // Resize the FormTableControl to have the rows and columns height/width refreshed immediately
    _table.widthValue(tab.widthValue());
    _table.heightValue(tab.heightValue());
}

public void init()
{
    WrkCtrResourceGroup     wrkCtrResourceGroup;

    formController = WrkCtrResourceAbilityMapController::construct();

    if (element.args().dataset() == tableNum(WrkCtrResourceGroup))
    {
        wrkCtrResourceGroup = element.args().record();
        if (wrkCtrResourceGroup.RecId)
        {
            if (WrkCtrResourceGroupResource::resourceGroupMembers(wrkCtrResourceGroup.RecId).RecId)
            {
                formController.parmWrkCtrGroupIdFilter(wrkCtrResourceGroup.WrkCtrId);
            }
            else
            {
                throw error("@SYS314245");
            }
        }
    }

    super();

    CapabilityTabPage.activatePage();
    this.loadData();
}

public void loadData()
{
    // Load the data
    formController.loadData();

    // Show data on the active grid
    this.refreshActiveGrid();
}

public void refreshActiveGrid(boolean _forceRefresh = false)
{
    if (_forceRefresh)
    {
        // Mark all data to be refreshed the next time the tab is activated
        formController.setAbilityDataChanged(WrkCtrActivityRequirementType::Capability, true);
        formController.setAbilityDataChanged(WrkCtrActivityRequirementType::Skill, true);
        formController.setAbilityDataChanged(WrkCtrActivityRequirementType::Course, true);
        formController.setAbilityDataChanged(WrkCtrActivityRequirementType::Certificate, true);
        formController.setAbilityDataChanged(WrkCtrActivityRequirementType::PersonTitle, true);
    }

    switch (true)
    {
        case capabilityTabPage.isActivePage():
            this.fillTable(capabilityTable, WrkCtrActivityRequirementType::Capability);
            break;
        case skillTabPage.isActivePage():
            this.fillTable(skillTable, WrkCtrActivityRequirementType::Skill);
            break;
        case courseTabPage.isActivePage():
            this.fillTable(courseTable, WrkCtrActivityRequirementType::Course);
            break;
        case certificateTabPage.isActivePage():
            this.fillTable(certificateTable, WrkCtrActivityRequirementType::Certificate);
            break;
        case titleTabPage.isActivePage():
            this.fillTable(titleTable, WrkCtrActivityRequirementType::PersonTitle);
            break;
    }

    this.enableActionPaneButtons();
}

