void clicked()
{
    Args    args;
    Common  recordToFind;

    recordToFind = WrkCtrCapability::findName(element.abilityId());

    args = new Args();
    args.caller(this);
    args.lookupRecord(recordToFind);
    new MenuFunction(menuitemDisplayStr(WrkCtrCapability), MenuItemType::Display).run(args);
}

void clicked()
{
    Args                    args;
    FormRun                 formRun;
    HcmWorker               hcmWorker;
    DirPerson               dirPerson;
    HcmPersonSkill          hcmPersonSkill;
    HcmSkill                hcmSkill;
    super();

    hcmWorker = HcmWorker::find(WrkCtrTable::find(element.resourceId()).Worker);
    dirPerson = DirPerson::find(hcmWorker.Person);

    select firstonly hcmPersonSkill
        join  SkillId from hcmSkill
        where hcmPersonSkill.Person     == dirPerson.RecId
           && hcmSkill.SkillId          == element.abilityId()
           && hcmSkill.RecId            == hcmPersonSkill.Skill;

    args = new Args();
    args.record(dirPerson);

    args.caller(this);
    formRun = new MenuFunction(new DictTable(tableNum(HcmPersonSkill)).formRef(), MenuItemType::Display).create(args);
    formRun.run();
    if (hcmPersonSkill)
    {
        formRun.dataSource().findRecord(hcmPersonSkill);
    }

    formRun.detach();
}

void clicked()
{
    Args                    args;
    FormRun                 formRun;
    WrkCtrTable             wrkCtrTable;
    HcmWorker               hcmWorker;
    DirPerson               dirPerson;
    HcmPersonCourse         hcmPersonCourse;

    super();

    wrkCtrTable = WrkCtrTable::find(element.resourceId());

    hcmWorker = HcmWorker::find(wrkCtrTable.Worker);
    dirPerson = DirPerson::find(hcmWorker.Person);

    select firstonly hcmPersonCourse
        where hcmPersonCourse.CourseId          == element.abilityId()
           && hcmPersonCourse.CourseDataAreaId  == wrkCtrTable.DataAreaId
           && hcmPersonCourse.Person            == dirPerson.RecId;

    args = new Args();
    args.record(dirPerson);
    args.caller(this);

    formRun = new MenuFunction(new DictTable(tableNum(HcmPersonCourse)).formRef(), MenuItemType::Display).create(args);
    formRun.run();
    if (hcmPersonCourse)
    {
        formRun.dataSource().findRecord(hcmPersonCourse);
    }

    formRun.detach();
}

void clicked()
{
    Args                    args;
    FormRun                 formRun;
    HcmWorker               hcmWorker;
    DirPerson               dirPerson;
    HcmPersonCertificate    hcmPersonCertificate;
    HcmCertificateType      hcmCertificateType;
    super();

    hcmWorker = HcmWorker::find(WrkCtrTable::find(element.resourceId()).Worker);
    dirPerson = DirPerson::find(hcmWorker.Person);

    select firstonly hcmPersonCertificate
        join  CertificateTypeId from hcmCertificateType
        where hcmPersonCertificate.CertificateType  == hcmCertificateType.RecId
           && hcmCertificateType.CertificateTypeId  == element.abilityId()
           && hcmPersonCertificate.Person           == dirPerson.RecId;

    args = new Args();
    args.caller(this);
    args.record(dirPerson);

    formRun = new MenuFunction(new DictTable(tableNum(HcmPersonCertificate)).formRef(), MenuItemType::Display).create(args);
    formRun.run();
