public void close()
{
    super();

    dateEffectivenessViewStateResource = dateEffectivenessFormControllerResource.viewState();
    dateEffectivenessViewStateResourceCalendar = dateEffectivenessFormControllerResourceCalendar.viewState();
    xSysLastValue::saveLast(this);
}

public void init()
{
    WrkCtrResourceGroup wrkCtrResourceGroupLookup;

    super();

    if (element.args().lookupTable() == tableNum(WrkCtrResourceGroup) ||
        (element.args().lookupRecord() && element.args().lookupRecord().TableId == tableNum(WrkCtrResourceGroup)))
    {
        //
        // If this form is opened due to a "Go to main table" then the
        // lookup arguments must be modified to use the WrkCtrTable since this is the primary data source
        //
        if (element.args().lookupField() == fieldNum(WrkCtrResourceGroup, RecId))
        {
            wrkCtrResourceGroupLookup = WrkCtrResourceGroup::find(any2int64(element.args().lookupValue()));
        }
        else
        {
            if (element.args().lookupRecord().RecId)
            {
                wrkCtrResourceGroupLookup = element.args().lookupRecord();
            }
        }

        element.args().lookupTable(tableNum(WrkCtrTable));
        element.args().lookupField(fieldNum(WrkCtrTable,WrkCtrId));
        element.args().lookupValue(wrkCtrResourceGroupLookup.WrkCtrId);
        element.args().lookupRecord(null);
    }
    else if (element.args().caller()
        && element.args().dataset() == tableNum(PMFSequenceGroup))
    {
        seqGroupId = element.args().record().(fieldNum(PMFSequenceGroup,SequenceGroupId));
        element.args().lookupTable(tableNum(WrkCtrTable));
        element.args().lookupField(fieldNum(WrkCtrTable,pmfSequenceGroupId));
        element.args().lookupValue(seqGroupId);
        element.args().lookupRecord(null);
    }

    accountWIPIssue.parmPostingType(LedgerPostingType::ProdWIPIssue);
    accountWIPValuation.parmPostingType(LedgerPostingType::ProdWIPValuation);
    accountWrkCtrIssueOffset.parmPostingType(LedgerPostingType::ProdWrkCtrIssueOffsetAccount);
    accountWrkCtrIssue.parmPostingType(LedgerPostingType::ProdWrkCtrIssue);

    // Initialize form controllers for date effectiveness
    dateEffectivenessFormControllerResource
        = DateEffectivenessFormController::newFormControls(wrkCtrResourceGroupResource_ds,
                                                            viewModeTextResource,
                                                            buttonViewAllResource,
                                                            buttonViewCurrentResource,
                                                            buttonDeleteResource,
                                                            buttonEditResource);

    dateEffectivenessFormControllerResourceCalendar
        = DateEffectivenessFormController::newFormControls(wrkCtrResourceCalendar_ds,
                                                            viewModeTextResourceCalendar,
                                                            viewAllResourceCalendar,
                                                            viewCurrentResourceCalendar,
                                                            deleteResourceCalendar,
                                                            editResourceCalendar);

    xSysLastValue::getLast(this);

    dateEffectivenessFormControllerResource.setViewState(dateEffectivenessViewStateResource);
    dateEffectivenessFormControllerResourceCalendar.setViewState(dateEffectivenessViewStateResourceCalendar);
}

