public void setPrinterSettings()
{
    SRSPrintDestinationSettings         printSettings;
    WrkCtrResourceGroupPrinterSettings  workCellprinterSettings;

    select firstonly workCellprinterSettings
        where workCellprinterSettings.WrkCtrResourceGroup == wrkCtrResourceGroup.RecId;

    printSettings = new SRSPrintDestinationSettings(workCellprinterSettings.PrinterSettings);

    // Launch print dialog so the user can select the desired output.
    if (SrsReportRunUtil::showSettingsDialog(printSettings))
    {
        ttsbegin;
        select forupdate firstonly workCellprinterSettings
            where workCellprinterSettings.WrkCtrResourceGroup == wrkCtrResourceGroup.RecId;

        workCellprinterSettings.WrkCtrResourceGroup = wrkCtrResourceGroup.RecId;
        workCellprinterSettings.PrinterSettings     = printSettings.pack();
        workCellprinterSettings.write();
        ttscommit;
    }
}

public boolean unpack(container packedClass)
{
    int version = RunBase::getVersion(packedClass);

    switch (version)
    {
        case #CurrentVersion:
            [version,#CurrentList] = packedClass;
            return true;
        default :
            return false;
    }
}

public void updateDesign()
{
    boolean notUsedInActivePlan;
    leanScheduleGroup.enabled(wrkCtrResourceGroup.IsWorkCell);
    KanbanBoard.enabled(wrkCtrResourceGroup.IsWorkCell);
    buttonPrinterSettings.enabled(wrkCtrResourceGroup.IsWorkCell);

    wrkCtrResourceGroup_ds.object(fieldNum(WrkCtrResourceGroup, InputInventLocationId)).mandatory(wrkCtrResourceGroup.IsWorkCell);
    wrkCtrResourceGroup_ds.object(fieldNum(WrkCtrResourceGroup, OutputInventLocationId)).mandatory(wrkCtrResourceGroup.IsWorkCell);
    wrkCtrResourceGroup_ds.object(fieldNum(WrkCtrResourceGroup, InputWMSLocationId)).mandatory(wrkCtrResourceGroup.IsWorkCell);
    wrkCtrResourceGroup_ds.object(fieldNum(WrkCtrResourceGroup, OutputWMSLocationId)).mandatory(wrkCtrResourceGroup.IsWorkCell);

    notUsedInActivePlan = !WrkCtrResourceGroup.isUsedInActivePlan();

    wrkCtrResourceGroup_ds.object(fieldNum(WrkCtrResourceGroup, IsWorkCell)).enabled(notUsedInActivePlan);
    wrkCtrResourceGroup_ds.object(fieldNum(WrkCtrResourceGroup, InputInventLocationId)).enabled(notUsedInActivePlan);
    wrkCtrResourceGroup_ds.object(fieldNum(WrkCtrResourceGroup, InputWMSLocationId)).enabled(notUsedInActivePlan);
    wrkCtrResourceGroup_ds.object(fieldNum(WrkCtrResourceGroup, OutputInventLocationId)).enabled(notUsedInActivePlan);
    wrkCtrResourceGroup_ds.enableOutputWMSLocation(notUsedInActivePlan);

    workCellCapacityOverview.enabled(wrkCtrResourceGroup.IsWorkCell);
    leanWorkCellCapacity_ds.allowCreate(wrkCtrResourceGroup.IsWorkCell);
    workCellCapacityActionPane.enabled(wrkCtrResourceGroup.IsWorkCell);
}

