public boolean validateDelete()
{
    boolean ret = super();

    if (ret)
    {
        if (GanttDataLoadPerfOptimizationFeature::instance().isEnabled())
        {
            WrkCtrResourceGroupResource membership = this.cursor();
            WrkCtrTable resource = WrkCtrTable::find(membership.WrkCtrId);
            WrkCtrResourceGroupMembershipCache::removeValue([resource.RecId]);
        }

        // Deletion is done by the controller
        dateEffectivenessFormControllerResource.delete();
        element.refreshResourcesFastTab();
    }

    // Return false always to not also have the form try to delete the record.
    // This means that the Deleting and Deleted events will never be raised
    return false;
}

