public void clicked()
{
    Args            args;
    MenuFunction    menuFunction;
    FormRun         formRun;

    args = new Args();
    args.caller(this);
    args.record(WrkCtrTable::find(wrkCtrResourceGroupResource.WrkCtrId));

    menuFunction = new MenuFunction(menuitemDisplayStr(ProdRouteJobSched), MenuItemType::Display);

    formRun = menuFunction.create(args);
    formRun.run();
}

