[Form]
public class WrkCtrResourceRequirementPart extends FormRun
{
    WrkCtrIResourceRequirement callerForm;
    FormRun                    callerFormRun;
    FormObservableLink         requirementFieldObservable = new FormObservableLink();

}

public void initActivityDataSource(FormDataSource _formDataSource)
{
    SysDictTable sysDictTable = new SysDictTable(_formDataSource.table());

    if (!isConfigurationkeyEnabled(sysDictTable.configurationKeyId()))
    {
        _formDataSource.linkType(FormLinkType::Passive);
    }
}

public void init()
{
    super();

    callerForm = element.args().caller() as WrkCtrIResourceRequirement;
    callerFormRun = element.args().caller() as FormRun;

    if (!callerForm || !callerFormRun)
    {
        throw error ("@SCM:ResourceReqFormPartInitError");
    }

    boolean inReadOnlyMode = callerForm.isWrkCtrActivityRequirementReadOnly();
    if (!inReadOnlyMode)
    {
        callerFormRun.onTask += EventHandler(this.updateFormViewMode);
        ActionPaneRequirements.visible(!inReadOnlyMode);
    }
}

/// <summary>
/// Update the form view when the host form changes.
/// </summary>
/// <param name = "_hostForm">The form where this form part is hosted.</param>
/// <param name = "_taskId">The task ID to execute.</param>
private void updateFormViewMode(FormRun _hostForm, int _taskId)
{
    #Task
    switch (_taskId)
    {
        case #taskEditRecord:
        case #taskDoneEditRecord:
            element.task(_taskId);
            break;
    }
}

/// <summary>
/// Sets the caller form in edit mode if it is in view mode.
/// </summary>
public void setCallerFormInEditMode()
{
    #Task
    if (callerFormRun.inViewMode())
    {
        callerFormRun.task(#taskEditRecord);
    }
}

edit WrkCtrActivityRequirementEdit requirementEdit(
    boolean                                 _set,
    WrkCtrActivityRequirement               _wrkCtrActivityRequirement,
    WrkCtrActivityRequirementEdit           _wrkCtrActivityRequirementEdit)
{
    WrkCtrActivityRequirementEdit   wrkCtrActivityRequirementEdit;
    WrkCtrId                        wrkCtrId;

    wrkCtrActivityRequirementEdit = _wrkCtrActivityRequirement.requirementEdit(
                                                    _set,
                                                     (_wrkCtrActivityRequirement.RecId != 0 && _wrkCtrActivityRequirement.RecId != wrkCtrActivityRequirement.RecId),
                                                     this.activeSubtype(_wrkCtrActivityRequirement),
                                                     _wrkCtrActivityRequirementEdit);

    if (WrkCtrActivityRequirementInitFromWrkCtr::launchInitFromWrkCtrDialog(_set,
                                                                         wrkCtrActivityRequirement,
                                                                         _wrkCtrActivityRequirement,
                                                                         wrkCtrActivityResourceRequirement,
                                                                         wrkCtrActivityResourceGroupRequirement))
    {
        wrkCtrId = _wrkCtrActivityRequirement.RelationshipType == WrkCtrActivityRequirementType::Resource ? wrkCtrActivityResourceRequirement.WrkCtrId : WrkCtrResourceGroup::find(wrkCtrActivityResourceGroupRequirement.ResourceGroup).WrkCtrId;
        callerForm.onWrkCtrIdChanged(wrkCtrId);
    }

    return wrkCtrActivityRequirementEdit;
}

public void write()
{
    Common          activeSubtype = this.activeSubtype();
    FormDataSource  fds;

    ttsbegin;

    if (activeSubtype.RecId != 0)
    {
        fds = FormDataUtil::getFormDataSource(activeSubtype);
        fds.write();
    }

    super();

    if (activeSubtype.RecId == 0)
    {
        activeSubtype.setActivityRequirement(wrkCtrActivityRequirement.RecId);
        activeSubtype.write();

        fds = FormDataUtil::getFormDataSource(activeSubtype);
        fds.reread();
    }

    ttscommit;
}

public void changeDisplay()
{
    capabilityOptions.visible(wrkCtrActivityRequirement.RelationshipType == WrkCtrActivityRequirementType::Capability);
    skillOptions.visible(wrkCtrActivityRequirement.RelationshipType == WrkCtrActivityRequirementType::Skill);

    wrkCtrActivityRequirement_ds.object(fieldNum(WrkCtrActivityRequirement, UsedForOperationScheduling)).allowEdit(wrkCtrActivityRequirement.allowEditSchedulingFlags());
    wrkCtrActivityRequirement_ds.object(fieldNum(WrkCtrActivityRequirement, UsedForJobScheduling)).allowEdit(wrkCtrActivityRequirement.allowEditSchedulingFlags());
}

public int active()
{
    int ret;
    boolean allow;

    this.changeDisplay();

    ret = super();

    allow = callerForm.allowCreateEditDeleteResourceRequirement();

    wrkCtrActivityRequirement_ds.allowCreate(allow);
    wrkCtrActivityRequirement_ds.allowEdit(allow);
    wrkCtrActivityRequirement_ds.allowDelete(allow);

    wrkCtrActivitySkillRequirement_ds.allowCreate(allow);
    wrkCtrActivitySkillRequirement_ds.allowEdit(allow);
    wrkCtrActivitySkillRequirement_ds.allowDelete(allow);

    wrkCtrActivityCapabilityRequirement_ds.allowCreate(allow);
    wrkCtrActivityCapabilityRequirement_ds.allowEdit(allow);
    wrkCtrActivityCapabilityRequirement_ds.allowDelete(allow);

    return ret;
}

public boolean validateWrite()
{
    boolean ret;
    Common  activeSubtype = this.activeSubtype();

    ret = super();

    if (ret && requirementEditGrid.text() == "")
    {
        // Field Requirement must be filled in
        ret = checkFailed(strFmt("@SYS312088", "@SYS8498", "@SYS11370"));
    }

    if (ret && activeSubtype.RecId == 0)
    {
        //Set a temporary link to ActivityRequirement to not fail the validateWrite because of this
        activeSubtype.setActivityRequirement(-1);

        ret = activeSubtype.validateWrite();
        ret = ret && activeSubtype.validateWriteRequirementSet(wrkCtrActivityRequirement.ActivityRequirementSet);

        activeSubtype.setActivityRequirement(0);
    }

    return ret;
}

public Common activeSubtype(WrkCtrActivityRequirement _wrkCtrActivityRequirement = wrkCtrActivityRequirement)
{
    boolean returnEmptyBuffer = true;

    if (_wrkCtrActivityRequirement.RecId || _wrkCtrActivityRequirement.RecId == wrkCtrActivityRequirement.RecId)
    {
        returnEmptyBuffer = false;
    }

    return _wrkCtrActivityRequirement.determineSubtype(  wrkCtrActivityCapabilityRequirement,
                                                        wrkCtrActivityCertificateRequirement,
                                                        wrkCtrActivityCourseRequirement,
                                                        wrkCtrActivityResourceRequirement,
                                                        wrkCtrActivityResourceGroupRequirement,
                                                        wrkCtrActivitySkillRequirement,
                                                        wrkCtrActivityResourceTypeRequirement,
                                                        wrkCtrActivityPersonTitleRequirement,
                                                        returnEmptyBuffer);
}

display Description requirementDescription(WrkCtrActivityRequirement _wrkCtrActivityRequirement)
{
    requirementFieldObservable.observe();

    return _wrkCtrActivityRequirement.subtype().requirementDescription();
}

