public void linkActive()
{
    super();

    boolean allow = callerForm.allowCreateEditDeleteResourceRequirement();
    wrkCtrActivityRequirement_ds.allowCreate(allow && callerForm.getWrkCtrActivityRequirementSet().RecId);
}

