    DateEffectivenessFormController             dateEffectivenessFormControllerResourceGroup;
    DateEffectivenessViewState                  dateEffectivenessViewStateResourceGroup;

    DateEffectivenessFormController             dateEffectivenessFormControllerCalendar;
    DateEffectivenessViewState                  dateEffectivenessViewStateCalendar;

    [SysObsolete('This field is not used anymore as the related toggle has been removed', false, 6\04\2021)]
    boolean                                     isWrkCtrChangeGroupModeToggle = true;

    #define.CurrentVersion(2)
    #localmacro.CurrentList
            dateEffectivenessViewStateCapability,
            dateEffectivenessViewStateResourceGroup,
            dateEffectivenessViewStateCalendar
        #endmacro

}

public void close()
{
    super();

    if (dateEffectivenessFormControllerCapability != null
        && dateEffectivenessFormControllerResourceGroup != null
        && dateEffectivenessFormControllerCalendar != null)
    {
        dateEffectivenessViewStateCapability = dateEffectivenessFormControllerCapability.viewState();
        dateEffectivenessViewStateResourceGroup = dateEffectivenessFormControllerResourceGroup.viewState();
        dateEffectivenessViewStateCalendar = dateEffectivenessFormControllerCalendar.viewState();
        xSysLastValue::saveLast(this);
    }
}

public void enableFields()
{
    boolean allowChange = wrkCtrTable.allowEmplVendorChange();
    boolean allowWorkerChange = wrkCtrTable.allowWorkerChange();

    wrkCtrTable_ds.object(fieldNum(WrkCtrTable,VendId)).allowEdit(allowChange && wrkCtrTable.WrkCtrType == WrkCtrType::Vendor);
    wrkCtrTable_ds.object(fieldNum(WrkCtrTable,Worker)).allowEdit(allowChange && wrkCtrTable.WrkCtrType == WrkCtrType::Personnel && allowWorkerChange);
    wrkCtrTable_ds.object(fieldNum(WrkCtrTable,WrkCtrType)).allowEdit(allowWorkerChange && wrkCtrTable.allowResourceTypeChange());
}

void init()
{
    WrkCtrId    lookupWrkCtrId;
    WrkCtrTable lookupWrkCtrTable;
    FormRun     formRun;

    form.ChangeGroupMode(ChangeGroupMode::ImplicitInnerOuter);

    super();

    // If the lookup is not for an individual resource then redirect to the resource group form
    if (element.args().caller())
    {
        if (element.args().lookupField() == fieldNum(WrkCtrTable, WrkCtrId))
        {
            lookupWrkCtrId = element.args().lookupValue();
            lookupWrkCtrTable = WrkCtrTable::find(lookupWrkCtrId);
        }

        if (lookupWrkCtrTable && !lookupWrkCtrTable.IsIndividualResource)
        {
            element.args().name(formStr(WrkCtrResourceGroup));

            formRun = classfactory.formRunClass(element.args());
            formRun.init();
            formRun.run();
            formRun.detach();

            element.close();
            return;
        }
    }

    if (!GanttClient_WrkCtr::isConfigurationKeyEnabled())
    {
        ganttWrkCtrBtn.visible(false);
    }

    accountWIPIssue.parmPostingType(LedgerPostingType::ProdWIPIssue);

    accountWIPValuation.parmPostingType(LedgerPostingType::ProdWIPValuation);

    accountWrkCtrIssueOffset.parmPostingType(LedgerPostingType::ProdWrkCtrIssueOffsetAccount);

    accountWrkCtrIssue.parmPostingType(LedgerPostingType::ProdWrkCtrIssue);

    // Initialize two form controllers for date effectiveness
    dateEffectivenessFormControllerCapability = DateEffectivenessFormController::newFormControls(wrkCtrCapabilityResource_ds,
                                                                                       viewModeTextCapability,
                                                                                       buttonViewAllCapability,
                                                                                       buttonViewCurrentCapability,
                                                                                       buttonDeleteCapability,
                                                                                       buttonEditCapability);

    dateEffectivenessFormControllerResourceGroup = DateEffectivenessFormController::newFormControls(wrkCtrResourceGroupResource_ds,
                                                                                       viewModeTextResourceGroup,
                                                                                       buttonViewAllResourceGroup,
                                                                                       buttonViewCurrentResourceGroup,
                                                                                       buttonDeleteResourceGroup,
                                                                                       buttonEditResourceGroup);

    dateEffectivenessFormControllerCalendar = DateEffectivenessFormController::newFormControls(wrkCtrResourceCalendar_ds,
                                                                                       viewModeTextResourceCalendar,
                                                                                       viewAllResourceCalendar,
                                                                                       viewCurrentResourceCalendar,
                                                                                       deleteResourceCalendar,
                                                                                       editResourceCalendar);

    xSysLastValue::getLast(this);

    dateEffectivenessFormControllerCapability.setViewState(dateEffectivenessViewStateCapability);
    dateEffectivenessFormControllerResourceGroup.setViewState(dateEffectivenessViewStateResourceGroup);
    dateEffectivenessFormControllerCalendar.setViewState(dateEffectivenessViewStateCalendar);
}

