public void init()
{

    super();

    //Limit to only display individual resources
    //a range on IsIndividualResource will already exist for fields that use WrkCtrResourceGroup EDT, so it must be overridden
    QueryBuildRange range = SysQuery::findOrCreateRange(this.query().dataSourceTable(tableNum(WrkCtrTable)),
                                                        fieldNum(WrkCtrTable, IsIndividualResource));
    range.value(queryValue(NoYes::Yes));
}

public Common resolveReference(FormReferenceControl _formReferenceControl)
{
    Common             ret;

    ret = super(_formReferenceControl);

    if (ret.RecId && !HcmWorkerLookup::newAllWorkersWithinCompany().validateWorker(ret.RecId))
    {
        ret.clear();
    }

    return ret;
}

public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    return HcmWorkerLookup::newAllWorkersWithinCompany().lookupWorker(_formReferenceControl);
}

