[Form]
public class projProjectTransactionsListPage extends FormRun implements OfficeIMenuCustomizer
{
    boolean includeSubProjectInitialized = false;

    QueryBuildDataSource qb_RootDataSource;
    QueryBuildDataSource qb_SalesDataSource1;
    QueryBuildDataSource qb_SalesDataSource2;
    QueryBuildDataSource qb_LinePropertyDataSource;
    QueryBuildRange criteriaDateRange;
    QueryBuildRange criteriaTransStatus;
    QueryBuildRange criteriaTransStatus2;
    QueryBuildRange criteriaToBeInvoiced;
    QueryBuildRange criteriaTimeMaterial;
    QueryBuildDataSource qb_NonChargeLinePropertyDS;
    QueryBuildRange criteriaLinePropertyId;

    private boolean enableProjMultipleContractLinesForProjectFeature = ProjMultipleContractLinesForProjectFeatureHelper::isFeatureEnabled();
    private boolean projOperationsIntegrationFeatureEnabled = ProjProjectOperationsIntegrationFeatureHelper::isFeatureEnabled();
    private boolean isIncludeDeductionsFeatureEnabled = FeatureStateProvider::isFeatureEnabled(ProjTransOnAccountIncludeDeductionsFeature::instance());

    private int64 adjustmentMessageThreshold;
    private int64 recordsSelected;

}

public void init()
{
    super();

    if (element.projOperationsIntegrationFeatureEnabled)
    {
        Inventory.visible(false);
        Inventory.enabled(false);
        AdjustmentTrace.visible(false);
        AdjustmentTrace.enabled(false);
        ButtonGroupNew.visible(false);
        ButtonGroupNew.enabled(false);
        BillGroup.visible(false);
        BillGroup.enabled(false);
        PostCosts.visible(false);
        PostCosts.enabled(false);
        AccrueRevenue.visible(false);
        AccrueRevenue.enabled(false);
        IsSplitTransaction.visible(false);
        IsSplitTransaction.enabled(false);

        if (ProjPostTransView_1_ds)
        {
            ProjPostTransView_1_ds.object(fieldNum(ProjPostTransView, PSAIndirectComponentGroup)).visible(false);
            ProjPostTransView_1_ds.object(fieldNum(ProjPostTransView, ResourceName)).visible(true);
            ProjPostTransView_1_TransactionGroupId.visible(true);
        }

        if (ResourceView_ds)
        {
            ResourceView_ds.object(fieldNum(ResourceView, Name)).visible(false);
        }
    }
    else if (ProjPostTransView_1_ds)
    {
        ProjPostTransView_1_ds.object(fieldNum(ProjPostTransView, ResourceName)).visible(false);
    }

    if (ProjPostTransView_1_ds && FeatureStateProvider::isFeatureEnabled(ProjCancelItemReqPackingSlipFeature::instance()))
    {
        ProjPostTransView_1_ds.object(fieldNum(ProjPostTransView, TransIdPackSlip)).visible(true);
        ProjPostTransView_1_TransIdPackSlip.visible(true);
    }

    IncludeDeductions.visible(isIncludeDeductionsFeatureEnabled);
}

/// <summary>
/// Runs once, first time executeQuery on the root data source is run.  Checks to see if we have a range on ProjId, and if we do then
/// makes the IncludeSubProjects check box visible.  Gets the value for this checkbox from SysLastValue and modifies the ProjId range
/// accordingly.
/// </summary>
private void initializeIncludeSubProjects()
{
    QueryBuildRange projIdRange = ProjPostTransView_1_ds.query().dataSourceTable(tablenum(ProjPostTransView)).findRange(fieldNum(ProjPostTransView, ProjId));

    boolean includeSubProjectsVisible = projIdRange && !element.projOperationsIntegrationFeatureEnabled;

    IncludeSubProjects.visible(includeSubProjectsVisible);

    if (includeSubProjectsVisible)
    {
        boolean include = this.getIncludeSubProjects();
        IncludeSubProjects.checked(include);

        if (include)
        {
            this.modifyProjectRange(projIdRange, include);
        }
    }
    else
    {
        IncludeSubProjects.enabled(false);
    }
}

/// <summary>
/// Customizes the options used to populate the Office Menu.
/// </summary>
/// <param name = "_menuOptions">The menu options to be customized.</param>
public void customizeMenuOptions(OfficeMenuOptions _menuOptions)
{
    // Clear the menu options which are driven by the root datasource of an entity, as some of the options are valid for sales quotation types.
    ListIterator dataEntityIterator = new ListIterator(_menuOptions.dataEntityOptions());

    while (dataEntityIterator.more())
    {
        if (dataEntityIterator.value().dataEntityName() == dataentityviewstr(ProjPostTransViewEntity))
        {
            dataEntityIterator.delete();
            break;
        }

        dataEntityIterator.next();
    }
}

private void filterAll()
{
    qb_SalesDataSource1.enabled(false);
    qb_SalesDataSource2.enabled(false);
    qb_LinePropertyDataSource.enabled(false);
    qb_NonChargeLinePropertyDS.enabled(false);
}

/// <summary>
/// This method is important for when the query range is modified from the filter dialog.  User can change between Is exact and Begins with
/// (and other values) here.  We try to understand how the filter is modified, and update the IncludeSubProjects checkbox accordingly.
/// </summary>
private void setIncludeSubProjectsCheckboxFromQueryRange()
{
    // display checkbox if we have a range on project id, otherwise hide the checkbox
    QueryBuildRange projIdRange = ProjPostTransView_1_ds.queryRunQueryBuildDataSource().findRange(fieldnum(ProjPostTransView, ProjId));

    if (projIdRange)
    {
        boolean include = strEndsWith(projIdRange.value(), '*');

        IncludeSubProjects.value(include);
        this.saveIncludeSubProjects(include);
    }
}

/// <summary>
/// Modifies the query to conditionally include or exclude deductions based upon whether the IncludeDeductions checkbox is checked or unchecked.
/// </summary>
protected void setIncludeDeductionsFromCheckbox()
{
    boolean includeDeductionsChecked = isIncludeDeductionsFeatureEnabled && IncludeDeductions.checked();

    //
    // The query uses a not exists join on its ProjOnAccTransView data source to filter out deductions.
    // Deductions are omitted when this query datasource is enabled, and included when it is disabled.
    //
    ProjPostTransView_1_ds.query().dataSourceTable(tableNum(ProjOnAccTransView)).enabled(!includeDeductionsChecked);
}

/// <summary>
/// Modifies project range when the IncludeSubProjects checkbox is checked or unchecked.
/// </summary>
private void setIncludeSubProjectsFromCheckbox()
{
    boolean includeSubProjectsChecked = IncludeSubProjects.checked();

    element.saveIncludeSubProjects(includeSubProjectsChecked);

    QueryBuildRange projIdRange = ProjPostTransView_1_ds.queryRunQueryBuildDataSource().findRange(fieldnum(ProjPostTransView, ProjId));

    if (projIdRange)
    {
        QueryBuildDataSource updateDataSource = ProjPostTransView_1_ds.query().dataSourceTable(tablenum(ProjPostTransView));
        QueryBuildRange updateRange = SysQuery::findOrCreateRange(updateDataSource , fieldnum(ProjPostTransView, ProjId));

        this.modifyProjectRange(updateRange, includeSubProjectsChecked);
    }
}

/// <summary>
/// Helper method, trim away and leading or trailing asterisks and set the new range.
/// </summary>
/// <param name = "_range">Range to modify</param>
/// <param name = "_includeSubProjects">Whether range should include subprojects</param>
private void modifyProjectRange(QueryBuildRange _range, boolean _includeSubProjects)
{
    str projId = _range.value();

    // remove leading & trailing asterisks, if appropriate
    while (strStartsWith(projId, '*'))  // this matters if the original range was 'contains', or 'valueLike' instead of 'begins with' or 'valueLikeAfter'
    {
        projId = strRem(substr(projId, 2, strLen(projId) - 1), '\\');
    }

    while (strEndsWith(projId, '*'))
    {
        projId = strRem(substr(projId, 1, strlen(projId) - 1), '\\');
    }

    if (_includeSubProjects)
    {
        _range.value(SysQuery::valueLikeAfter(projId));
    }
    else
    {
        _range.value(SysQuery::value(projId));
    }
}

/// <summary>
/// Save the value of the IncludeSubProjects to <c>SysLastValue</c>.  Make sure the parameters to putValue matches the values to getValue in the
/// matching getIncludeSubProjects method.
/// </summary>
/// <param name = "_includeSubProjects">value to save to <c>SysLastValue</c></param>
private void saveIncludeSubProjects(boolean _includeSubProjects)
{
    xSysLastValue::putValue([_includeSubProjects], curExt(), curUserId(), UtilElementType::Form, element.form().name(), 'IncludeSubProjects');
}

/// <summary>
/// Get the value of the IncludeSubProjects from <c>SysLastValue</c>.  Make sure the parameters to getValue matches the values to putValue in the
/// matching saveIncludeSubProjects method.
/// </summary>
/// <returns>Value read from <c>SysLastValue</c></returns>
private boolean getIncludeSubProjects()
{
    boolean include;

    [include] = xSysLastValue::getValue(curExt(), curUserId(), UtilElementType::Form, element.form().name(), 'IncludeSubProjects');

    return include;
}

