public void init()
{
    super();

    if (element.projOperationsIntegrationFeatureEnabled)
    {
        ProjAdjustment.visible(false);
    }
    else
    {
        ProjAdjustment.visible(true);
    }

    includeSubProjectInitialized = false;

    qb_RootDataSource = projPostTransView_1_ds.query().dataSourceTable(tableNum(ProjPostTransView));

    // Primary query to use to filter based on sales status. This is part of the view
    qb_SalesDataSource1 = projPostTransView_1_ds.query().dataSourceTable(tableNum(ProjPostTransSaleView));

    // Secondary query used to filter based on sales status
    qb_SalesDataSource2 = qb_RootDataSource.addDataSource(tableNum(ProjPostTransSaleView));
    qb_SalesDataSource2.addLink(fieldNum(ProjPostTransView, TransId), fieldNum(ProjPostTransSaleView, TransId));
    qb_SalesDataSource2.joinMode(JoinMode::ExistsJoin);

    qb_LinePropertyDataSource = projPostTransView_1_ds.query().dataSourceTable(tableNum(ProjLineProperty));

    QueryBuildDataSource qb_projTable = qb_RootDataSource.addDataSource(tableNum(ProjTable));
    qb_projTable.addLink(fieldNum(ProjPostTransView,ProjId), fieldNum(ProjTable, ProjId));
    qb_projTable.fetchMode(0);
    qb_projTable.joinMode(JoinMode::ExistsJoin);

    // To add view as datasource used to filter nonchargeable records based on transid
    qb_NonChargeLinePropertyDS = qb_SalesDataSource1.addDataSource(tableNum(ProjPostedNonChargeableTrans));

    if (!element.enableProjMultipleContractLinesForProjectFeature)
    {
        criteriaTimeMaterial        = qb_projTable.addRange(fieldNum(ProjTable, Type));
    }

    element.filterAll();

    boolean vendPostProjTransFeatureEnabled = FeatureStateProvider::isFeatureEnabled(projProjectTransactionsListPageVendFeature::instance());

    ProjPostTransView_1_VendorName.visible(vendPostProjTransFeatureEnabled);
    ProjPostTransView_1_VendorAccount.visible(vendPostProjTransFeatureEnabled);
}

public void executeQuery()
{
    if (!includeSubProjectInitialized)
    {
        element.initializeIncludeSubProjects();
    }

    super();

    if (includeSubProjectInitialized)
    {
        element.setIncludeSubProjectsCheckboxFromQueryRange();
    }
    else
    {
        includeSubProjectInitialized = true;
    }
}

