public boolean modified()
{
    boolean ret;

    QueryBuildRange         transDateRange;
    TransDate               queryStartDate;
    TransDate               queryEndDate;

    transDateRange = SysQuery::findOrCreateRange(qb_RootDataSource, fieldNum(ProjPostTransView, TransDate));
    queryStartDate = transStartDate.dateValue();
    queryEndDate = transEndDate.dateValue();

    transDateRange.value(SysQuery::range(queryStartDate, queryEndDate));
    projPostTransView_1_ds.executeQuery();

    ret = super();

    return ret;
}

public boolean modified()
{
    boolean ret;

    QueryBuildRange         transDateRange;
    TransDate               queryStartDate;
    TransDate               queryEndDate;

    transDateRange = SysQuery::findOrCreateRange(qb_RootDataSource, fieldNum(ProjPostTransView, TransDate));
    queryStartDate = transStartDate.dateValue();
    queryEndDate = transEndDate.dateValue();

    transDateRange.value(SysQuery::range(queryStartDate, queryEndDate));
    projPostTransView_1_ds.executeQuery();

    ret = super();

    return ret;
}

public boolean modified()
{
    boolean ret;
    ret = super();

    qb_RootDataSource.clearRange(fieldNum(ProjPostTransView, LinePropertyId));
    qb_SalesDataSource1.clearRange(fieldNum(ProjPostTransSaleView, TransStatus));
    qb_NonChargeLinePropertyDS.clearLinks();

    criteriaTransStatus = SysQuery::findOrCreateRange(qb_SalesDataSource1, fieldNum(ProjPostTransSaleView, TransStatus));
    criteriaTransStatus2 = SysQuery::findOrCreateRange(qb_SalesDataSource2, fieldNum(ProjPostTransSaleView, TransStatus));
    criteriaLinePropertyId = SysQuery::findOrCreateRange(qb_RootDataSource, fieldNum(ProjPostTransView, LinePropertyId));

    // Fetches the project type value of selected project from <c>ProjTable</c> table.
    ProjTable projTable;
    QueryBuildRange projIdRange = ProjPostTransView_1_ds.queryRunQueryBuildDataSource().findRange(fieldnum(ProjPostTransView, ProjId));
    if (projIdRange)
    {
        select firstOnly Type from projTable
            where projTable.ProjId == projIdRange.value();
    }

    switch (this.selection())
    {
        case projSelectTrans::All:
            element.filterAll();
            break;

        case projSelectTrans::Invoiced:
            qb_LinePropertyDataSource.enabled(false);
            qb_NonChargeLinePropertyDS.enabled(false);

            criteriaLinePropertyId.value(SysQuery::valueNotEmptyString());

            qb_SalesDataSource1.enabled(true);
            criteriaTransStatus.value(strFmt('%1, %2, %3, %4, %5',
                                        queryValue(ProjTransStatus::Invoiced),
                                        queryValue(ProjTransStatus::Eliminated),
                                        queryValue(ProjTransStatus::Estimated),
                                        queryValue(ProjTransStatus::MarkedCreditnote),
                                        queryValue(ProjTransStatus::CreditnoteProposal)));
            // Non-Invoiced
            qb_SalesDataSource2.joinMode(JoinMode::NoExistsJoin);
            qb_SalesDataSource2.enabled(true);
            criteriaTransStatus2.value(strFmt('%1, %2',
                                        queryValue(ProjTransStatus::Posted),
                                        queryValue(ProjTransStatus::Registered)));
            break;

        case projSelectTrans::PartiallyInvoiced:
            qb_LinePropertyDataSource.enabled(false);
            qb_NonChargeLinePropertyDS.enabled(false);

            // Invoiced
            qb_SalesDataSource1.enabled(true);
            criteriaTransStatus.value(strFmt('%1, %2, %3, %4, %5',
                                        queryValue(ProjTransStatus::Invoiced),
                                        queryValue(ProjTransStatus::Eliminated),
                                        queryValue(ProjTransStatus::Estimated),
                                        queryValue(ProjTransStatus::MarkedCreditnote),
                                        queryValue(ProjTransStatus::CreditnoteProposal)));
            // Non-Invoiced
            qb_SalesDataSource2.enabled(true);
            qb_SalesDataSource2.joinMode(JoinMode::ExistsJoin);
            criteriaTransStatus2.value(strFmt('%1, %2',
                                        queryValue(ProjTransStatus::Posted),
                                        queryValue(ProjTransStatus::Registered)));
            break;

        case projSelectTrans::Adjusted:
            qb_NonChargeLinePropertyDS.enabled(false);

            qb_SalesDataSource1.enabled(true);
            criteriaTransStatus.value(queryValue(ProjTransStatus::Adjusted));

            qb_SalesDataSource2.enabled(false);
            qb_LinePropertyDataSource.enabled(false);
            break;

        case projSelectTrans::ToBeInvoiced:
            qb_NonChargeLinePropertyDS.enabled(false);

            // To consider the line property filter for time and material project.
            if (projTable.Type == ProjType::TimeMaterial || projTable.Type == ProjType::None)
            {
                if (criteriaTimeMaterial && !element.enableProjMultipleContractLinesForProjectFeature)
                {
                    criteriaTimeMaterial.value(queryValue(ProjType::TimeMaterial));
                }
                qb_SalesDataSource1.enabled(true);
                criteriaTransStatus.value(strFmt("%1,%2,%3",
                                            queryValue(ProjTransStatus::Registered),
                                            queryValue(ProjTransStatus::Posted),
                                            queryValue(ProjTransStatus::InvoiceProposal)));

                qb_LinePropertyDataSource.enabled(true);
                qb_LinePropertyDataSource.joinMode(JoinMode::NoExistsJoin);
                criteriaToBeInvoiced =  SysQuery::findOrCreateRange(qb_LinePropertyDataSource, fieldNum(ProjLineProperty, ToBeInvoiced));
                criteriaToBeInvoiced.enabled(true);
                criteriaToBeInvoiced.value(SysQuery::value(NoYes::No));

                qb_SalesDataSource2.enabled(false);
            }
            else
            {
                qb_SalesDataSource1.enabled(true);
                criteriaTransStatus.value(queryValue(ProjTransStatus::NoStatus));
                qb_LinePropertyDataSource.enabled(false);
                qb_SalesDataSource2.enabled(false);
            }
            break;

        case projSelectTrans::NoChargeable:
            qb_SalesDataSource2.enabled(false);
            qb_LinePropertyDataSource.enabled(false);

            if (criteriaTimeMaterial && !element.enableProjMultipleContractLinesForProjectFeature)
            {
                criteriaTimeMaterial.value(SysQuery::valueUnlimited());
            }

            qb_SalesDataSource1.enabled(true);
            qb_NonChargeLinePropertyDS.enabled(true);

            // To filter the non chargeable transactions
            qb_NonChargeLinePropertyDS.addlink(fieldNum(ProjPostTransSaleView, TransId), fieldNum(ProjPostedNonChargeableTrans, TransId));
            qb_NonChargeLinePropertyDS.joinMode(JoinMode::ExistsJoin);
            break;

        default:
            //If nothing, do same as All
            qb_SalesDataSource1.enabled(false);
            qb_SalesDataSource2.enabled(false);
            qb_LinePropertyDataSource.enabled(false);
            qb_NonChargeLinePropertyDS.enabled(false);
            break;
    }

    projPostTransView_1_ds.executeQuery();

    return ret;
}

