// Enable general tab convert buttons.
public void enableApplicableGeneralConvertButtons(boolean _isCustomer, boolean _isVendor)
{
    boolean                 enableCustomer, enableVendor, isB2BProspect;
    smmBusRelTypeGroup      busRelTypeGroup = smmBusRelTypeGroup::find(smmBusRelTable.BusRelTypeId);

    isB2BProspect = busRelTypeGroup.TableType == smmBusRelTypeSourceTable::B2BProspect;

    enableCustomer = smmBusRelTable && (busRelTypeGroup.TableType != smmBusRelTypeSourceTable::Vendor) 
        && !isB2BProspect && !_isCustomer;
    Convert2CustomerCtrl.enabled(enableCustomer);

    enableVendor = smmBusRelTable && (busRelTypeGroup.TableType != smmBusRelTypeSourceTable::Customer) 
        && !isB2BProspect && !_isVendor;
    Convert2VendorCtrl.enabled(enableVendor);

    this.updateElementsForB2BProspect(isB2BProspect);
}

private void updateElementsForB2BProspect(boolean _isB2BProspect)
{
    Convert2CustomerCtrl.visible(!_isB2BProspect);
    Convert2VendorCtrl.visible(!_isB2BProspect);
    ApproveMenuBtnCtrl.visible(_isB2BProspect);
    RejectCtrl.visible(_isB2BProspect);
    ResetCtrl.visible(_isB2BProspect);
    ResendEmailCtrl.visible(_isB2BProspect);
    SendEmailCtrl.visible(false);
    B2BRetailGroup.visible(_isB2BProspect);

    boolean isRejected, isApproved, isPending = false;

    if (_isB2BProspect)
    {
        RetailB2BProspectStatus _prospectStatus = RetailSmmBusRelTable::findBySmmBusRelTableRefRecId(smmBusRelTable.RecId).B2BProspectStatus;
        isRejected = _prospectStatus == RetailB2BProspectStatus::Rejected;
        isApproved = _prospectStatus == RetailB2BProspectStatus::Approved;
        isPending = _prospectStatus == RetailB2BProspectStatus::Pending;

        ApproveMenuBtnCtrl.enabled(isPending);
        RejectCtrl.enabled(isPending);
        ResetCtrl.enabled(isApproved || isRejected);
        ResendEmailCtrl.enabled(isApproved || isRejected);
        SendEmailCtrl.enabled(false);

        // If the B2B Prospects has been approved, check whether we have sent an email to them before or whether one is pending.
        if (isApproved)
        {
            // `RetailEventNotificationAction` maintains list of notifications that are pending and haven't been sent out.
            RetailEventNotificationAction rena = RetailEventNotificationAction::findByRefRecId(smmBusRelTable.RecId);
            boolean notificationIsPending = rena.RecId != 0;

            if (notificationIsPending)
            {
                // Although notification is pending, we still want to show the 'Resend email' button to maintain current behavior.
                ResendEmailCtrl.visible(notificationIsPending);
                ResendEmailCtrl.enabled(notificationIsPending);
                SendEmailCtrl.visible(!notificationIsPending);
                SendEmailCtrl.enabled(!notificationIsPending);
            }

            // If no notification is pending, check if we've already sent an email.
            else
            {
                // Get the email address associated with this B2B prospect.
                LogisticsElectronicAddress lea = LogisticsElectronicAddress::findByDirPartyRecId(smmBusRelTable.Party, false);
                if (lea)
                {
                    // Search `RetailEventNotificationLog` for any entries with their email address.
                    // If one is found, it means that we've already sent an email to them so keep the 'Resend email' button visible.
                    RetailEventNotificationLog notificationLog;
                    select firstonly RecId from notificationLog
                        where notificationLog.ReceiverEmailAddress == lea.Locator;

                    boolean notificationHasBeenSent = notificationLog.RecId != 0;

                    ResendEmailCtrl.visible(notificationHasBeenSent);
                    ResendEmailCtrl.enabled(notificationHasBeenSent);
                    SendEmailCtrl.visible(!notificationHasBeenSent);
                    SendEmailCtrl.enabled(!notificationHasBeenSent);
                }
            }
        }
    }
}

