private void enableApplicableSellButtons()
{
    CustTable           custTable;
    smmLeadTable        smmLeadTableRecords;
    smmOpportunityTable smmOpportunityTable;
    SalesQuotationTable salesQuotation;
    smmBusRelTypeId     custGroup;

    select firstonly Party from smmLeadTableRecords
        where smmLeadTableRecords.Party == smmBusRelTable.Party;

    mibSmmLeadsListPage.enabled(smmLeadTableRecords.RecId != 0);

    select firstonly Party from smmOpportunityTable
        where smmOpportunityTable.Party == smmBusRelTable.Party;

    mibSmmOpportunitiesListPage.enabled(smmOpportunityTable.RecId != 0);

    custGroup = smmBusRelTypeGroup::getCustomerType();
    if (smmBusRelTable.BusRelTypeId == custGroup)
    {
        select firstonly RecId from salesQuotation
            exists join custTable where custTable.Party == smmBusRelTable.Party &&
            custTable.AccountNum == salesQuotation.CustAccount;
    }
    else
    {
        select firstonly RecId from salesQuotation
            where salesQuotation.BusRelAccount == smmBusRelTable.BusRelAccount;
    }

    SalesQuotationListPage.enabled(salesQuotation.RecId != 0);
}

