/// <summary>
///     Checks if the passed record ID should be merged with another customer.
/// </summary>
/// <param name="_dupRecId">
///     The duplicate <c>DirPartyTable</c> record ID to be merged with another customer.
/// </param>
/// <remarks>
///     If the user decides to merge the existing customer with another,
///     the recId of the duplicate is passed to this method.
/// </remarks>
public void mcrDuplicateRecId(RecId _dupRecId)
{
    CustTable localCustTable = CustTable::findByPartyRecId(DirPartyTable::findRec(_dupRecId).RecId);

    busRelAcctSwitched = smmBusRelTable::findCust(localCustTable.AccountNum).BusRelAccount;

    // Record is new and the customer has chosen to merge it so
    // it should be deleted.
    if (smmBusRelTable.RecId == 0)
    {
        smmBusRelTable_ds.reread();
        smmBusRelTable_ds.delete();
    }

    smmBusRelTable_ds.executeQuery();
}

public void mcrDuplicateUsed(smmBusRelTable result)
{
    if (result)
    {
        if (smmBusRelTableFindRecordFlightIsEnabled)
        {
            smmBusRelTable_ds.findRecord(result);
        }
        else
        {
            smmBusRelTable_ds.positionToRecord(result);
        }
        smmBusRelTable.data(result);
        smmBusRelTable_ds.active();
        smmBusRelTable_ds.research(true);
    }
}

/// <summary>
///     Calls returns for a business relation that is a customer.
/// </summary>
void mcrReturnInquiry()
{
    MCRReturnTableConfigure returnTableConfigure = new MCRReturnTableConfigure();
    returnTableConfigure.parmCustAccount(smmBusRelTable.custAccount());

    Args formArgs = new Args();
    formArgs.parmObject(returnTableConfigure);
    formArgs.caller(this);

    FormRun formRun = new MenuFunction(menuitemDisplayStr(ReturnTableDetails), MenuItemType::Display).create(formArgs);
    formRun.run();
    formRun.wait();
}

