public void initValue()
{
    super();

    if (element.args())
    {
        callerRecord = element.args().record();

        if (callerRecord.TableId == tableNum(DirPartyTable))
        {
            dirPartyTable.data(callerRecord);

            if (dirPartyTable.type() == DirPartyType::Person)
            {
                dirPersonName.data(DirPersonName::find(dirPartyTable.RecId));
            }
            smmBusRelTable.Party = dirPartyTable.RecId;
            smmBusRelTable.modifiedField(fieldNum(smmBusRelTable,Party));
        }
    }
}

int active()
{
    int ret;
    boolean isCustomer;
    boolean isVendor;

    if (isConfigurationkeyEnabled(configurationKeyNum(MCRCallCenter)))
    {
        // Need this re-run so the call to enable controls below
        // correctly hides or shows the List tab.
        mcrSmmBusRelationListTable_ds.executeQuery();
    }

    ret = super();

    if (ret)
    {
        partyForm.parmPartyType(dirPartyTable.type());
        element.updateControls();
        element.updateDesign();

        isCustomer = DirPartyTableHelper::isCustomer(smmBusRelTable.Party);
        isVendor   = DirPartyTableHelper::isvendor(smmBusRelTable.Party);

        element.enableApplicableGeneralConvertButtons(isCustomer, isVendor);                                 
        element.enableApplicableGeneralRelInfoButtons(isCustomer, isVendor);
        element.enableApplicableSellButtons();
        element.enableApplicableMarketButtons();
        element.enableApplicableActivityButtons();
        element.enablemcrRelatedGroupRelated(isCustomer);
    }

    return ret;
}

