}

void init()
{
    super();

    // Enable caching of the document handling and note it display fields
    smmBusRelTable_ds.cacheAddMethod(tableMethodStr(smmBusRelTable, showDocHanIcon));

    if (isConfigurationkeyEnabled(configurationKeyNum(MCRCallCenter)))
    {
        smmBusRelTable_ds.cacheAddMethod(tableMethodStr(MCRsmmBusRelTable, displayIsMergedBusRel));
    }

    // Ensure, it is not possible to create a Business Relation, when opening this form from Contact Person Form
    if (element.args() && element.args().record() && element.args().record().TableId == tableNum(ContactPerson))
    {
        smmBusRelTable_ds.allowCreate(false);
    }
}

public void linkActive()
{
    smmResponsibilityGroup  responsibilityGroup;
    element.numberSeqFormHandler().formMethodDataSourceLinkActive();
    // Is form called with a record
    if (element.args() && element.args().record())
    {
        switch (element.args().dataset())
        {
            // Is the form called from the activities form
            case tableNum(smmActivities) :
                smmActivityParentLink::updateQuery(this.query().dataSourceTable(tableNum(smmBusRelTable)), element.args().record());
                break;

            // Is the form called from the responsibilities form
            case tableNum(smmResponsibilityGroup) :
                // Get the current selected responsibilities record
                responsibilityGroup  = element.args().record();

                // Update query
                smmResponsibility::updateQuery(this.query(),this.table(),responsibilityGroup.ResponsibilityId);
                break;

            case tableNum(smmLeadTable):
                smmLeadTable = element.args().record();
                break;
        }
    }
    super();
}

public boolean validateWrite()
{
    boolean         ret;

    ret = super();
    ret = element.numberSeqFormHandler().formMethodDataSourceValidateWrite(ret) && ret;

    if (ret)
    {
        ret = partyForm.validateWrite();
    }

    return ret;
}

void write()
{
    ttsbegin;

    element.numberSeqFormHandler().formMethodDataSourceWrite();

    if (!dirPartyTable.RecId)
    {
        dirPartyTable_ds.write();
    }

    smmBusRelTable.Party = dirPartyTable.RecId;

    super();

    ttscommit;
}

public void selectionChanged()
{
    super();
    executeAddressQuery = true;
    executeContactInfoQuery = true;
}

public boolean validate()
{
    boolean ret;

    ret = super();

    if (ret)
    {
        if (smmBusRelTable.BusRelTypeId && smmBusRelTypeGroup::find(smmBusRelTable.BusRelTypeId).TableType != smmBusRelTypeSourceTable::Relation)
        {
            // Cannot create business relation of type customer/vendor directly.
            ret = checkFailed("@SYS111990");
        }
    }

    return ret;
}

public void modified()
{
    const str blank = '';

    super();

    // When segment changes, subsegment should be cleared
    smmBusRelTable.Subsegment = blank;

    // Refersh business relation data source
    smmBusRelTable_ds.refresh();
}

public boolean validateWrite()
{
    boolean ret = true;

    ret = dirPersonName.validateWrite();

    if (ret)
    {
        ret = super();
    }

    return ret;
}

public void write()
{
    if (!(dirPartyTable is DirPerson))
    {
        return;
    }

    dirPartyTable_ds.write();

    dirPersonName.Person = dirPartyTable.RecId;
    super();
    dirPartyTable_ds.reread();
}

