void distributeByResponsibility(TableId _tableId)
{
    Dialog          d;
    Caption         caption;
    DialogField     dialogResponsibilities;
    int             numberOfUpdatedTargets;
    int             unassignedTargets;
    switch (_tableId)
    {
        case tableNum(smmBusRelTable) :
            caption = "@SYS82439"; // Distribute by responsibilites on business relaions
            break;

        case tableNum(CustTable) :
            caption = "@SYS108434"; // Distribute by responsibilites on customers
            break;

        case tableNum(VendTable) :
            caption = "@SYS108435"; // Distribute by responsibilites on vendors
            break;

        default :
            return;
    }
    d = new Dialog(caption);
    dialogResponsibilities  = d.addField(extendedTypeStr(smmResponsibilityId));

    // The campaign is already started and targets have responded. Should the targets still be distributed?
    if (!smmCampaignSelection::isStarted(smmCampaignTable.CampaignId) || Box::yesNo("@SYS87135", DialogButton::Yes) == DialogButton::Yes)
    {
        if (d.run())
        {
            // Check that responsibility field is filled out
            if (!dialogResponsibilities.value())
            {
                // A responsiblitiy criteria must be selected
                throw error("@SYS82440");
            }
            else
            {
                numberOfUpdatedTargets = smmCampaignTable::distributeByResponsibilityOnRelation(_tableId,smmCampaignTable.CampaignId, dialogResponsibilities.value(), false);

                if (numberOfUpdatedTargets > 0)
                {
                    // Responsible employee on %1 campaign targets are updated
                    info(strFmt("@SYS86947", numberOfUpdatedTargets));
                }
                else
                {
                    // No targets were updated
                    info("@SYS87158");
                }

                // Find number of unassigned targets left on the campaign
                unassignedTargets = smmCampaignSelection::countNumbersOfUnassignedTargets(smmCampaignTable.CampaignId);

                // Is there unassigned targets
                if (unassignedTargets > 0)
                {
                    // There are still targets that are not assigned to a employee
                    warning("@SYS87137");
                }

                // Only execute query if targets were updated
                if (numberOfUpdatedTargets > 0)
                {
                    smmCampaignSelection_ds.executeQuery();
                }
            }
        }
    }
}

void distributeMyMainResponsible(TableId _tableId)
{
    int numberOfUpdatedTargets;
    int unassignedTargets;

    // The campaign is already started and targets have responded. Should the targets still be distributed?
    if (!smmCampaignSelection::isStarted(smmCampaignTable.CampaignId) || Box::yesNo("@SYS87135", DialogButton::Yes) == DialogButton::Yes)
    {
        // Start the distribution and return the number of affected targets
        numberOfUpdatedTargets = smmCampaignTable::distributeByMainContact(_tableId,smmCampaignTable.CampaignId, false);

        if (numberOfUpdatedTargets > 0)
        {
            // Responsible employee on %1 campaign targets are updated
            info(strFmt("@SYS86947", numberOfUpdatedTargets));
        }
        else
        {
            // No targets were updated
            info("@SYS87158");
        }

        // Find number of unassigned targets left on the campaign
        unassignedTargets = smmCampaignSelection::countNumbersOfUnassignedTargets(smmCampaignTable.CampaignId);

        // Is there unassigned targets
        if (unassignedTargets > 0)
        {
            // There are still targets that are not assigned to a employee
            warning("@SYS87137");
        }

        // Only execute query if targets were update
        if (numberOfUpdatedTargets > 0)
        {
            smmCampaignSelection_ds.executeQuery();
        }
    }
}

