void enableSalesOrderButton()
{
    boolean enableSalesOrderButton = false;

    if (smmCampaignSelection)
    {
        smmBusRelTableForTarget = smmBusRelTable::findByParty(smmCampaignSelection.party);

        if (smmBusRelTableForTarget.RecId == 0 || smmBusRelTableForTarget.VendAccount())
        {
            enableSalesOrderButton = false;
        }
        else
        {
            enableSalesOrderButton = true;
        }
    }

    MCRSalesTable.enabled(enableSalesOrderButton);
}

void enableTableGroupMenuItems()
{
    boolean                 isCampaignClosedOrCancelled = smmCampaignTable.isCampaignClosedOrCancelled();
    boolean                 enableLeads;
    boolean                 enableOpportunities;
    smmLeadRelTable         smmLeadRelTable;
    smmOpportunityRelTable  smmOpportunityRelTable;

    // Set state of the leads button
    select firstonly RecId from smmLeadRelTable
        where smmLeadRelTable.RefRecId   == smmCampaignTable.RecId &&
                smmLeadRelTable.RefTableId == smmCampaignTable.TableId;
    enableLeads = (smmLeadRelTable.RecId != 0);
    smmLeadsListPage.enabled(enableLeads);

    // Set state of opportunities button
    select firstonly RecId from smmOpportunityRelTable
        where smmOpportunityRelTable.RefRecId   == smmCampaignTable.RecId &&
              smmOpportunityRelTable.RefTableId == smmCampaignTable.TableId;
    enableOpportunities = (smmOpportunityRelTable.RecId != 0);
    smmOpportunitiesListPage.enabled(enableOpportunities);

    // Set state of the project buttons
    project.enabled(smmCampaignTable.ProjId != '');
    campaign2Project.enabled(smmCampaignTable.ProjId != '');

    // Set state of the telemarketing button
    telemarketing.enabled(smmCampaignTable.tmCallListID != '');

    kmCollection.enabled(smmCampaignTable.kmCollectionId != '');

    campaign2Project.enabled(!isCampaignClosedOrCancelled);
    campaign2NewProject.enabled(!isCampaignClosedOrCancelled);
    create.enabled(!isCampaignClosedOrCancelled);
    broadcast.enabled(!isCampaignClosedOrCancelled);
    target.enabled(!isCampaignClosedOrCancelled);
    distribute.enabled(!isCampaignClosedOrCancelled);

    this.enableFunctionButtonsForTargets();
}

/// <summary>
/// Enable edit operation for the campaign record only when the campaign is not in closed or cancelled status and the form is in edit mode.
/// </summary>
public void setEditMode()
{
    boolean canEditCampaign = !smmCampaignTable.isCampaignClosedOrCancelled() && !element.inViewMode();

    smmCampaignTable_ds.allowEdit(canEditCampaign);
    smmCampaignSelection_ds.allowEdit(canEditCampaign);
    smmCampaignSelection_ds.allowCreate(canEditCampaign);
    smmCampaignSelection_ds.allowDelete(canEditCampaign);
}

