void init()
{
    #define.JumpToMain('JumpToMain')

    smmCampaignTable        parentCampaignTable;
    smmCampaignSelection    selection;
    smmTMCallListGroup      smmTMCallListGroup;
    smmCampaignTable        campaign;
    HcmWorker               hcmWorker;
    QueryBuildDataSource    queryBuildDataSource;

    if (element.args().parm() == #JumpToMain)
    {
        jumpToMain  = NoYes::No;
    }
    else
    {
        jumpToMain  = NoYes::Yes;
    }

    // Is form called from another campaign (to show sub campaigns)
    if (!jumpToMain && element.args().parmObject())
    {
        campaignParams = element.args().parmObject();

        if (campaignParams)
        {
            parentCampaignTable = campaignParams.campaignTable();
            parentCampaignId    = parentCampaignTable.CampaignId;
        }

        element.smmCampaignId(parentCampaignTable.CampaignId);
    }

    super();

    if (element.args().parmObject())
    {
        emplSelection = 0;
    }


    if (element.args().record())
    {
        switch (element.args().record().TableId)
        {
            case tableNum(smmTMCallListGroup):
                smmTMCallListGroup = element.args().record();
                queryBuildDataSource =  smmCampaignTable_ds.query().dataSourceTable(tableNum(smmCampaignTable));
                rangeCallListID = queryBuildDataSource.addRange(fieldNum(smmCampaignTable, tmCallListID));
                rangeCallListID.value(smmTMCallListGroup.CallListID);
                break;
            case tableNum(smmCampaignSelection):
                selection       = element.args().record();
                campaign        = smmCampaignTable::find(selection.CampaignId);
                hcmWorker       = HcmWorker::find(campaign.CampaignRespWorker);
                emplSelection   = hcmWorker.RecId;
                break;
            case tableNum(smmBusRelTable):
                smmBusRelTable  = element.args().record();
                break;
            case tableNum(SalesQuotationTable):
                callerCampaignId   = (element.args().record() as SalesQuotationTable).CampaignId;
                break;
            case tableNum(SalesTable):
                callerCampaignId   = (element.args().record() as SalesTable).smmCampaignId;
                break;
        }
    }

    this.setFilters();

    if (!smmCampaignTableDoNotExecuteQueryOnInitFlight::instance().isEnabled())
    {
        smmCampaignTable_ds.executeQuery();
    }

    hcmWorkerLookupCurrentCompany = HcmWorkerLookup::newCustomOptions(true, true, true, false, true, true, true, true, true);
}

