void setFilters()
{
    if (formHasMethod(element.args().caller(), identifierStr(CampaignProcess)) )
    {
        selectedProcess = element.args().caller().campaignProcess();
    }
    if (formHasMethod(element.args().caller(), identifierStr(CampaignRespWorker)) )
    {
        emplSelection = element.args().caller().campaignResponsibleWorker();
    }
    if (formHasMethod(element.args().caller(), identifierStr(campaignId)) )
    {
        if (!callerCampaignId)
            callerCampaignId = element.args().caller().campaignId();
    }


}

void setFocusOnCampaign(smmCampaignTable _campaignTable)
{
    activeCampaignId = activeCampaignId ? activeCampaignId : smmCampaignTable.CampaignId;

    // Set focus on the created campaign
    if (smmCampaignTable.CampaignId != _campaignTable.CampaignId)
    {
        smmCampaignTable_ds.positionToRecordByValue(fieldNum(smmCampaignTable, CampaignId), activeCampaignId);
    }
    else
    {
        smmCampaignTable_ds.positionToRecordByValue(fieldNum(smmCampaignTable, CampaignId), _campaignTable.CampaignId);
    }
}

