public boolean setProcessInstance(HierarchyName _oldProcess, HierarchyName _newProcess)
{
    DialogButton    answer = DialogButton::Yes;

    if (_oldProcess && _oldProcess !=_newProcess)
    {
        // Do you want to remove all open activities?
        answer = Box::yesNoCancel("@SYS109839", DialogButton::Cancel);

        if (answer == DialogButton::Cancel)
        {
            return checkFailed("@SYS25904"); //Update has been canceled.
        }
    }

    smmProcessInstance::write(smmCampaignTable, _oldProcess, _newProcess, false, answer == DialogButton::Yes);

    return true;
}

void setQuestionnaireAccess()
{
    // Are there a questionaire connected to the campaign
    if (smmCampaignTable.kmCollectionId)
    {
        mbQuestionnaire.enabled(true);

        // If there are responses to the campaign, questionnaire should be aceessable
        if (smmCampaignSelection.Responded)
        {
