void updateSubCampaigns(smmCampaignStatus _status, smmCampaignReasonId _reason, NoYes _closed)
{
    if (smmCampaignTable.Mask)
    {
        if (_status != smmCampaignStatus::Cancelled && _status != smmCampaignStatus::Closed)
        {
            // Should subcampaigns also be updated?
            if (Box::yesNo("@SYS82785", DialogButton::Yes) == DialogButton::No)
            {
                return;
            }
        }

        // Update status on the sub campaigns
        smmCampaignTable::traverseCampaigns(smmCampaignTable.CampaignId, _status, _reason, _closed);
    }
}

/// <summary>
/// Find the newly created record and switch the form to details view edit mode.
/// </summary>
/// <param name = "_smmCampaignTableRecord">Record buffer to which context will be set.</param>
public void setContext(smmCampaignTable _smmCampaignTableRecord)
{
    #Task

    smmCampaignTable_DS.research();
    smmCampaignTable_DS.positionToRecord(_smmCampaignTableRecord);

    // Switch the form to details view and edit mode
    this.task(#taskSwitchToDetailsView);
    if (element.inViewMode())
    {
        this.task(#taskEditRecord);
    }
}

public int task(int _taskId)
{
    int ret;

    ret = super(_taskId);

    if (smmCampaignTable.RecId && (_taskId == #taskEditRecord || _taskId == #taskDoneEditRecord))
    {
        element.setEditMode();
    }

    return ret;
}

public void initValue()
{
    super();
    smmCampaignTable.Worker = smmUtility::getCurrentContactWorker();
}

public void validateCampaignDates()
{
    if ((smmCampaignTable.CampaignEndDate) && (smmCampaignTable.CampaignEndDate < smmCampaignTable.CampaignDate))
    {
        //The End date you have entered is before the Campaign date.
        throw error("@SYS106572");
    }
    if ((smmCampaignTable.CampaignFollowUpDate) && (smmCampaignTable.CampaignFollowUpDate < smmCampaignTable.CampaignDate))
    {
        //The Follow-up date you have entered is before the Campaign date.
        throw error("@SYS106573");
    }
}

public void write()
{
    smmActivities   smmActivities;
    HierarchyName   oldProcess;
    boolean         ret;

    try
    {
        smmCampaignTable.updateFromAdvPromptActivity(smmCampaignTable.orig());
        oldProcess = smmCampaignTable.orig().ProcessName;

        super();

        // Campaign process
        ret = element.setProcessInstance(oldProcess, smmCampaignTable.ProcessName);

        if (!ret)
        {
            smmCampaignTable_ds.reread();
        }
    }
    catch
    {
        if (smmCampaignTable.CampaignFollowUpActivity
            && smmCampaignTable.orig().CampaignFollowUpActivity != smmCampaignTable.CampaignFollowUpActivity)
        {
            ttsbegin;
            smmCampaignTable.CampaignFollowUpActivity = smmCampaignTable.orig().CampaignFollowUpActivity;
            smmActivities = smmActivities::find(smmCampaignTable.CampaignFollowUpActivity,true);
            if (smmActivities)
            {
                smmActivities.delete(false);
            }
            ttscommit;
        }
    }
}

public void delete()
{
    super();

    // Set buttons after campaign is deleted
    element.setButtonAccess();
    element.enableFunctionButtonsForCampaigns(false);
}

int active()
{
    int  ret;

    ret = super();

    element.setButtonAccess();

    element.enableFunctionButtonsForCampaigns(smmCampaignTable.RecId ? true : false);
    element.enableApplicableStatusButtons();

    if (smmCampaignTable.RecId)
    {
        element.enableTableGroupMenuItems();
        element.setEditMode();
    }

    // Campaign process
    element.setProcessControls();

    return ret;
}

void executeQuery()
{
    QueryBuildDataSource    queryBuildDataSource;

    queryBuildDataSource =  smmCampaignTable_ds.query().dataSourceTable(tableNum(smmCampaignTable));

    processRange    = SysQuery::findOrCreateRange(queryBuildDataSource, fieldNum(smmCampaignTable, ProcessName));
    processRange.value(selectedProcess);

    if (callerCampaignId)
    {
        campaignIdRange    = SysQuery::findOrCreateRange(queryBuildDataSource, fieldNum(smmCampaignTable, CampaignId));
        campaignIdRange.value(callerCampaignId);
    }

    super();

    element.setButtonAccess();
    element.enableFunctionButtonsForCampaigns(false);
    element.setProcessControls();
}

void init()
{
    super();

    // Enable caching of the document handling and note it display fields
    smmCampaignTable_ds.cacheAddMethod(tableMethodStr(smmCampaignTable, showDocHanIcon));

    if (element.args().caller())
    {
        emplSelection = str2int64(SysQuery::valueUnlimited());
    }
}

public void linkActive()
{
    Common                  callerRecord;
    tableId                 callerTableId;
    ContactPerson           contactPerson;
    smmResponsibilityGroup  responsibilityGroup;
    Hierarchy               hierarchy;
    QueryBuildDataSource    queryBuildDataSource;

    // Is form called with a record
    if (element.args() && element.args().record())
    {
        callerTableId = element.args().dataset();
        switch (callerTableId)
        {
            // Is the form called from the business relation/customer/vendor form
            case tableNum(smmBusRelTable) :
            case tableNum(CustTable) :
            case tableNum(VendTable) :

                // Get the current selected business relation/customer/vendor record
                callerRecord  = element.args().record();

                if (callerTableId == tableNum(smmBusRelTable))
                {
                    party = callerRecord.(fieldNum(smmBusRelTable,Party));
                }
                else if (callerTableId == tableNum(CustTable))
                {
                    party = callerRecord.(fieldNum(CustTable,Party));
                }
                else if (callerTableId == tableNum(VendTable))
                {
                    party = callerRecord.(fieldNum(VendTable,Party));
                }

                // Get the manual created campaign section datasource
                queryBuildDataSource = this.query().dataSourceTable(tableNum(smmCampaignSelection));

                // If the datasource isn't created already
                if (!queryBuildDataSource)
                {
                    // Add the extra campaign selection data source
                    queryBuildDataSource    = this.query().dataSourceNo(1).addDataSource(tableNum(smmCampaignSelection));

                    // Add a new range for filtering on party
                    rangeParty   = queryBuildDataSource.addRange(fieldNum(smmCampaignSelection, Party));

                    // Link the new datasource to the existing query
                    queryBuildDataSource.joinMode(JoinMode::ExistsJoin);
                    queryBuildDataSource.fetchMode(QueryFetchMode::One2One);
                    queryBuildDataSource.addLink(fieldNum(smmCampaignTable, CampaignId), fieldNum(smmCampaignSelection, CampaignId));

                    // The new datasource should be hidden in the query selection form
                    rangeParty.status(RangeStatus::Hidden);
                }

                // Filter on the current selected business relation
                rangeParty.value(queryValue(party));
                break;

            // Is the form called from the contact person form
            case tableNum(ContactPerson) :
                // Get the current selected contact person record
                contactPerson   = element.args().record();
                contactId       = contactPerson.ContactPersonId;

                // Get the manual created campaign section datasource
                queryBuildDataSource = this.query().dataSourceTable(tableNum(smmCampaignSelection));

                if (!queryBuildDataSource)
                {
                    // Add the extra campaign selection data source
                    queryBuildDataSource    = this.query().dataSourceNo(1).addDataSource(tableNum(smmCampaignSelection));

                    // Add a new range for filtering on contact person
                    rangeContactPerson      = queryBuildDataSource.addRange(fieldNum(smmCampaignSelection, ContactPersonId));

                    // Link the new datasource to the existing query
                    queryBuildDataSource.joinMode(JoinMode::ExistsJoin);
                    queryBuildDataSource.fetchMode(QueryFetchMode::One2One);
                    queryBuildDataSource.addLink(fieldNum(smmCampaignTable, CampaignId), fieldNum(smmCampaignSelection, CampaignId));

                    // The new datasource should be hidden in the query selection form
                    rangeContactPerson.status(RangeStatus::Hidden);
                }

                // The new datasource should be hidden in the query selection form
                rangeContactPerson.value(queryValue(contactId));
                break;

            // Is the form called from activity form
            case tableNum(smmActivities) :
                smmActivityParentLink::updateQuery(this.query().dataSourceTable(tableNum(smmCampaignTable)), element.args().record());
                break;

            // Is form called from Responsibilities form
            case tableNum(smmResponsibilityGroup) :
                // Get the current selected responsibilities record
                responsibilityGroup  = element.args().record();

                // Get the manual created responsibilities assignment datasource
                queryBuildDataSource = this.query().dataSourceTable(tableNum(smmResponsibilitiesEmplTable));

                // If the datasource isn't created already
                if (!queryBuildDataSource)
                {
                    // Add the extra responsibility assigment data source
                    queryBuildDataSource    = this.query().dataSourceTable(tableNum(smmCampaignTable)).addDataSource(tableNum(smmResponsibilitiesEmplTable));

                    // Link the new datasource to the existing query
                    queryBuildDataSource.joinMode(JoinMode::ExistsJoin);
                    queryBuildDataSource.fetchMode(QueryFetchMode::One2One);
                    queryBuildDataSource.addLink(fieldNum(smmCampaignTable, RecId), fieldNum(smmResponsibilitiesEmplTable, RefRecId));
                }

                rangeRefTableId   = queryBuildDataSource.findRange(fieldNum(smmResponsibilitiesEmplTable, RefTableId));
                if (!rangeRefTableId)
                {
                    rangeRefTableId   = queryBuildDataSource.addRange(fieldNum(smmResponsibilitiesEmplTable, RefTableId));
                }
                rangeRefTableId.status(RangeStatus::Hidden);

                rangeRespId       = queryBuildDataSource.findRange(fieldNum(smmResponsibilitiesEmplTable, ResponsibilityId));
                if (!rangeRespId)
                {
                    rangeRespId   = queryBuildDataSource.addRange(fieldNum(smmResponsibilitiesEmplTable, ResponsibilityId));
                }
                rangeRespId.status(RangeStatus::Hidden);

                // Filter on the responsibilities assigments for campaigns, selected responsibility
                rangeRefTableId.value(queryValue(tableNum(smmCampaignTable)));
                rangeRespId.value(queryValue(responsibilityGroup.ResponsibilityId));
                break;

            // Is the form called from the process template form
            case tableNum(Hierarchy) :
                hierarchy = element.args().record();
                selectedProcess = hierarchy.Name;
                break;
        }
    }
    super();
}

