    // Refresh the campaign table data source from another form
    smmCampaignTable_ds.reread();
}

void treeChange(FormTreeItem _newItem)
{
    smmCampaignTable campaignTable;

    campaignTable = smmCampaignTable::find(_newItem.data());
    element.smmCampaignId(campaignTable.CampaignParentId);

    if (campaignTable)
    {
        smmCampaignTable_ds.allowEdit(true);

        if ((smmCampaignParentId != element.smmCampaignId()))
        {
            smmCampaignParentId = smmCampaignTable::find(campaignTable.CampaignParentId).CampaignId;
            smmCampaignTable_ds.executeQuery();
        }
    }
    else
    {
        smmCampaignTable_ds.allowEdit(false);
        smmCampaignTable.clear();
    }

    smmCampaignTable_ds.positionToRecord(campaignTable);
    smmCampaignTable_ds.refresh();
}

public void create(boolean _append = false)
{
    Args                args;
    FormRun             formCreate;
    smmCampaignTable    newCampaignTable;

    // Clear the return value from the Campaign create form
    createsmmCampaignId = '';

    // Open the Campaign create form
    args = new Args();
    args.name(formStr(smmCampaignTableCreateNew));
    args.caller(element);
    args.record(smmCampaignTable);

    formCreate = classfactory.formRunClass(args);

    if (formCreate)
    {
        formCreate.init();
        formCreate.run();

        formCreate.wait();

        // Was a value returned from the Campaign create form
        if (!element.createsmmCampaignId())
        {
            element.closeCancel();
        }
    }
    parentCampaignId = "";
}

