public void write()
{
    smmActivities   smmActivities;

    try
    {
        smmCampaignSelection.updateFromAdvPromptActivity(smmCampaignSelection.orig());
        super();
    }
    catch
    {
        if (smmCampaignSelection.ActivityNumber
            && smmCampaignSelection.orig().ActivityNumber != smmCampaignSelection.ActivityNumber)
        {
            ttsbegin;
            smmCampaignSelection.ActivityNumber = smmCampaignSelection.orig().ActivityNumber;
            smmActivities = smmActivities::find(smmCampaignSelection.ActivityNumber,true);
            if (smmActivities)
            {
                smmActivities.delete(false);
            }
            ttscommit;
        }
    }
}

int active()
{
    int ret;

    ret = super();

    element.setKMKnowledgeCollectorUserResultsAccess();

    element.enableFunctionButtonsForTargets();

    element.setQuestionnaireAccess();

    element.enableSalesOrderButton();

    if (smmCampaignSelection.RecId)
    {
        smmCampaignSelection_ContactPersonId.allowEdit(false);
        editContactPersonName.allowEdit(false);
        smmCampaignSelection_Party.allowEdit(false);
    }
    else
    {
        smmCampaignSelection_ContactPersonId.allowEdit(true);
        editContactPersonName.allowEdit(true);
        smmCampaignSelection_Party.allowEdit(true);
    }

    return ret;
}

