public boolean validate()
{
    boolean ret;
    ContactPerson  contactPerson;

    ret = super();

    if (smmCampaignSelection.ContactPersonId)
    {
        select firstonly contactPerson
            where contactPerson.ContactPersonId == smmCampaignSelection.ContactPersonId
            && contactPerson.Inactive == NoYes::No;

        if (!contactPerson)
        {
            ret = checkFailed(strFmt("@SCM:TableFieldValidation", smmCampaignSelection.ContactPersonId, '@SYS14142', '@SYS14560'));
        }
    }

    return ret;                
}

public boolean validate()
{
    boolean             ret;
    smmCampaignSelection  smmCampaignSelection_Orig;

    smmCampaignSelection_Orig = smmCampaignSelection.orig();

    ret = super();

    // Warn user that attached activity will be deleted
    if (ret && !smmCampaignSelection.FollowUpRespWorker && smmCampaignSelection_Orig.FollowUpRespWorker && smmCampaignSelection.ActivityNumber)
    {
        ret = (DialogButton::Ok == Box::okCancel("@SYS90963",DialogButton::Ok));
    }

    return ret;
}

