void clicked()
{
    // Update to stage In process?
    if (Box::yesNo("@SYS81087", DialogButton::Yes) == DialogButton::Yes)
    {
        element.updateSubCampaigns(smmCampaignStatus::InProcess, '', NoYes::No);

        if (smmCampaignTable.MCRSourceID)
        {
            smmCampaignSelection    campaignSelection;
            CustTable               custTable;
            MCRCustTable            mcrCustTable;
            ContactPerson           contactPerson;

            ttsBegin;

            while select forupdate mcrCustTable
                exists join campaignSelection
                where campaignSelection.CampaignId == smmCampaignTable.CampaignId
                join contactPerson
                where campaignSelection.ContactPersonId == contactPerson.ContactPersonId
                join custTable
                where custTable.party           == contactPerson.ContactForParty
                   && mcrCustTable.custTable    == custTable.RecId
            {
                mcrCustTable.SourceIDLastPromoted = smmCampaignTable.MCRSourceID;
                mcrCustTable.doUpdate();
            }

            ttsCommit;
        }

        smmCampaignTable.CampaignStatus     = smmCampaignStatus::InProcess;
        smmCampaignTable.CampaignClosed     = NoYes::No;
        smmCampaignTable.CampaignReasonId   = '';

        smmCampaignTable.write();
        smmCampaignTable_ds.active();
    }

    super();
}

void clicked()
{
    // Update stage to Response?
    if (Box::yesNo("@SYS81086", DialogButton::Yes) == DialogButton::Yes)
    {
        element.updateSubCampaigns(smmCampaignStatus::Response, '', NoYes::No);

        smmCampaignTable.CampaignStatus     = smmCampaignStatus::Response;
        smmCampaignTable.CampaignClosed     = NoYes::No;
        smmCampaignTable.CampaignReasonId   = '';

        smmCampaignTable.write();
        smmCampaignTable_ds.active();
    }

    super();
}

void clicked()
{
    // Update stage to Follow-up?
    if (Box::yesNo("@SYS81112", DialogButton::Yes) == DialogButton::Yes)
    {
        element.updateSubCampaigns(smmCampaignStatus::FollowUp, '', NoYes::No);

        smmCampaignTable.CampaignStatus = smmCampaignStatus::FollowUp;
        smmCampaignTable.CampaignClosed = NoYes::No;
        smmCampaignTable.CampaignReasonId   = '';

        smmCampaignTable.write();
        smmCampaignTable_ds.active();
    }

    super();
}

void clicked()
{
    // Update stage to Closed?
    if (Box::yesNo("@SYS81085", DialogButton::Yes) == DialogButton::Yes)
    {
        // If there is a process make sure all exit criteria has been validated
        if (smmCampaignTable.ProcessName)
        {
            smmProcessInstance.changeStage(smmProcessInstance.getLastStage().ElementNumber, true);
        }

        if (!smmCampaignTable::haveAllTargetsResponded(smmCampaignTable.CampaignId))
        {
            // Not all targets have responded. Should the campaign be closed?
            if (Box::yesNo("@SYS88129", DialogButton::No) == DialogButton::No)
            {
                return;
            }
        }
        if (smmCampaignTable.isTargetActivitiesExists())
        {
            // Should activities for targets be closed?
            if (Box::yesNo("@SYS90577", DialogButton::No) == DialogButton::Yes)
            {
                smmCampaignTable.closeTargetActivities();
            }
        }

        if (smmCampaignTable.ProcessName)
        {
            // Do you want to remove all open activities?
            if (Box::yesNo("@SYS109839", DialogButton::No) == DialogButton::Yes)
            {
                smmProcessInstance = smmProcessInstance::construct(smmCampaignTable);
                smmProcessInstance.parmRemoveActivitiesOnly(true);
                smmProcessInstance.parmRemoveOpenActivities(true);
                smmProcessInstance.delete();

                // Delete the primary activities attached
                smmActivityParentLink::deletePrimaryActivities(smmCampaignTable,smmActivityParentType::Campaign);
            }
        }

        // This will affect all sub campaigns connected to the selected campaign. Proceed?
        if (smmCampaignTable.Mask && Box::yesNo("@SYS82786", DialogButton::No) == DialogButton::No)
        {
            return;
        }

        element.updateSubCampaigns(smmCampaignStatus::Closed, '', NoYes::Yes);

        smmCampaignTable.CampaignStatus     = smmCampaignStatus::Closed;
        smmCampaignTable.CampaignClosed     = NoYes::Yes;
        smmCampaignTable.CampaignReasonId   = '';

        smmCampaignTable.write();
        smmCampaignTable_ds.active();
    }

    super();
}

