/// <summary>
/// Converts the current campaign to a call list.
/// </summary>
void clicked()
{
    // Check that the campaign contains targets
    if (smmCampaignSelection::countNumbers(smmCampaignTable.CampaignId) == 0)
    {
        // Cannot convert a campaign without targets
        throw error("@SYS89141");
    }

    // Do you want to create a telemarketing call list based upon the targets?
    if (Box::yesNo("@SYS85016", DialogButton::Yes) == DialogButton::No)
    {
        // Call list creation was cancelled
        throw warning("@SYS37856");
    }

    // Create a new call list and transfer the campaign targets to the call list
    if (smmCampaignTable::transferCampaignTargetsToCallList(smmCampaignTable))
    {
        // Call list creation has been succesful - Campaign status will be set to Response.
        info("@SYS37857" + '\n' +  "@SYS81078");

        // Run the query again to show the updated campaign data
        Common currentBuffer = smmCampaignTable_ds.cursor().data();
        smmCampaignTable_ds.executeQuery();
        smmCampaignTable_ds.positionToRecord(currentBuffer);
    }
}

void clicked()
{
    // This will send an email to all targets in this campaign
    if (Box::okCancel("@SYS81089", DialogButton::Ok) == DialogButton::Cancel)
    {
        // Email broadcast was cancelled
        throw error("@SYS80589");
    }

    smmCampaignSelection_ds.broadcastCampaign();
    smmCampaignSelection_ds.executeQuery();

    Common currentBuffer = smmCampaignTable_ds.cursor().data();
    smmCampaignTable_ds.executeQuery();
    smmCampaignTable_ds.positionToRecord(currentBuffer);
}

