void clicked()
{
    int numberOfUpdatedTargets;
    int unassignedTargets;

    // The campaign is already started and targets have responded. Should the targets still be distributed?
    if (!smmCampaignSelection::isStarted(smmCampaignTable.CampaignId) || Box::yesNo("@SYS87135", DialogButton::Yes) == DialogButton::Yes)
    {
        numberOfUpdatedTargets = smmCampaignTable::distributeByMainContactOnContactPerson(smmCampaignTable.CampaignId, false);

        if (numberOfUpdatedTargets > 0)
        {
            // Responsible employee on %1 campaign targets are updated
            info(strFmt("@SYS86947", numberOfUpdatedTargets));
        }
        else
        {
            // No targets were updated
            info("@SYS87158");
        }

        // Find number of unassigned targets left on the campaign
        unassignedTargets = smmCampaignSelection::countNumbersOfUnassignedTargets(smmCampaignTable.CampaignId);

        // Is there unassigned targets
        if (unassignedTargets > 0)
        {
            // There are still targets that are not assigned to a employee
            warning("@SYS87137");
        }

        // Only execute query if targets were updated
        if (numberOfUpdatedTargets > 0)
        {
            smmCampaignSelection_ds.executeQuery();
        }
    }
}

