/// <summary>
/// Converts the prospect to a customer if the BR is a prospect.
/// </summary>
void clicked()
{
    NoYes       conversionSuccesfull;

    if (!smmBusRelTableForTarget.CustAccount()
        && !smmBusRelTableForTarget.mcrIsListDeleted())
    {
        // BR is a prospect & not flagged as deleted
        // BR is a prospect type - ask if the user wants to convert to a Customer
        // and if they choose no then they cannot be allowed to see/create SO's for
        // the contact.
        if (Box::yesNo("@MCR25854",dialogbutton::No, "@MCR25855") == DialogButton::Yes)
        {
            // Convert to customer
            conversionSuccesfull =  smmBusRelTable::convert2Customer(smmBusRelTableForTarget);

            if (conversionSuccesfull)
            {
                smmBusRelTable::MCROpenCustTable(smmBusRelTableForTarget);
            }
        }
        else // user choose not to convert the prospect to a customer - do nothing
        {
            return;
        }
    }
    // call the sales order form
    super();
}

void clicked()
{
    CustAccount         custAccount;
    smmBusRelAccount    busRelAccount;
    smmLeadTable        smmLeadTable;
    smmOpportunityTable smmOpportunityTable;
    custAccount = CustTable::findByPartyRecId(smmCampaignSelection.Party).AccountNum;
    busRelAccount = smmBusRelTable::findByParty(smmCampaignSelection.Party).BusRelAccount;

    party_BusRelTable.enabled(busRelAccount ? true : false);
    party_CustTable.enabled(custAccount ? true : false);
    party_VendTable.enabled(DirPartyTableHelper::isvendor(smmCampaignSelection.Party));
    smmQuotationCompetitorGroup.enabled(smmQuotationCompetitorGroup::find(smmCampaignSelection.Party) ? true : false);
    contactPerson1.enabled(smmCampaignSelection.ContactPersonId != '');

    activities.enabled(smmCampaignSelection.ActivityNumber ? true : false);

    quotation.enabled(
    (custAccount && SalesQuotationTable::existSales_custAccount(custAccount)) ||
    (busRelAccount && SalesQuotationTable::existSales_BusRelAccount(busRelAccount)) ||
    (custAccount && SalesQuotationTable::existProject_custAccount(custAccount)) ||
    (busRelAccount && SalesQuotationTable::existProject_BusRelAccount(busRelAccount)));

    select firstonly RecId from smmLeadTable where smmLeadTable.Party == smmCampaignSelection.Party;
    smmLeadTableButton.enabled(smmLeadTable.RecId ? true : false);

    select firstonly RecId from smmOpportunityTable where smmOpportunityTable.Party == smmCampaignSelection.Party;
    smmOpportunityTableButton.enabled(smmOpportunityTable.RecId ? true : false);
    super();
}

public void lookup()
{
    Args    args;
    FormRun formRun;
    ContactPerson   contactPerson;

    args = new Args();
    args.name(formStr(ContactPersonLookup));
    args.caller(smmCampaignSelection_ContactPersonId);
    args.lookupValue(smmCampaignSelection.ContactPersonId);
    formRun = classfactory.formRunClass(args);
    formRun.party(smmCampaignSelection.Party);
    formRun.callingNameControl(editContactPersonName);
    formRun.callingIdControl(this);
    formRun.init();

    formRun.selectRecordMode(smmCampaignSelection_ContactPersonId);
    smmCampaignSelection_ContactPersonId.performFormLookup(formRun);
    formRun.wait();

    if (formRun.closedOk())
    {
        contactPerson = formRun.selectRecordModeSelectedRecord();

        smmCampaignSelection.ContactPersonId = contactPerson.ContactPersonId;
        smmCampaignSelection_ContactPersonId.text(contactPerson.ContactPersonId);
        smmCampaignSelection.Party = contactPerson.ContactForParty;
        smmCampaignSelection_ds.rereadReferenceDataSources();
        editContactPersonName.enabled(false);
    }
}

