// This is the generic CreateEntity method which is called by
// Create customer and Create opportunity functions
//
void createEntity(smmEntityToCreate _entityCreate)
{
    // from Create Customer function button
    if (_entityCreate == smmEntityToCreate::Customer)
    {
        // Check license
        if (!smmLicense::custTableInsert())
        {
            // Not allowed to store data in Customer table
            throw error("@SYS80669");
        }

        smmUtility::openMenuItemForm(menuitemDisplayStr(CustTable),smmLeadTable,element,true);
    }
    else if (_entityCreate == smmEntityToCreate::Opportunity)
    {
        smmUtility::openMenuItemForm(menuitemDisplayStr(smmOpportunityTable),smmLeadTable,element,true);
    }

    // Refresh form
    smmLeadTable_ds.reread();
    smmLeadTable_ds.active();
}

public void createRecord(str _formDataSourceName, boolean _append = false)
{
    Map typesToCreate;
    smmBusRelTable smmbusreltab;

    if (_formDataSourceName == smmLeadTable_ds.name() ||
       _formDataSourceName == dirPartyTable_ds.name())
    {
        typesToCreate = new Map(Types::String, Types::String);
        typesToCreate.insert(smmLeadTable_ds.name(), tableStr(DirPartyTable));

        if (callerRecord && callerRecord is smmBusRelTable)
        {
            smmbusreltab = callerRecord as smmBusRelTable;
        }

        // New party
        if (smmbusreltab && smmbusreltab.partyType() == DirPartyType::Person)
        {
            typesToCreate.insert(dirPartyTable_ds.name(), tableStr(DirPerson));
        }
        else if (smmbusreltab && smmbusreltab.partyType() == DirPartyType::Organization)
        {
            typesToCreate.insert(dirPartyTable_ds.name(), tableStr(DirOrganization));
        }
        else if (DirAppParameters::find().BusRelDefaultPartyType == DirPartyBaseType::Person)
        {
            typesToCreate.insert(dirPartyTable_ds.name(), tableStr(DirPerson));
        }
        else
        {
            typesToCreate.insert(dirPartyTable_ds.name(), tableStr(DirOrganization));
        }

        smmLeadTable_ds.createTypes(typesToCreate);
    }
    else
    {
        super(_formDataSourceName, _append);
    }
}

public void enableApplicableGenerateButtons()
{
    boolean enableBusinessRelation  = (smmBusRelTable::findByParty(smmLeadTable.Party).RecId == 0);
    boolean enableCustomer          = (CustTable::findByPartyRecId(smmLeadTable.Party).RecId == 0) && (smmLeadTable.Status == smmLeadStatus::Qualified || smmLeadTable.Status == smmLeadStatus::Open);
    boolean enableOpportunities     = (smmLeadTable.Status == smmLeadStatus::Qualified || smmLeadTable.Status == smmLeadStatus::Open);

    createCustomerButton.enabled(enableCustomer);
    generateOpportunity.enabled(enableOpportunities);
}

public void enableApplicableRelatedInfoButtons()
{
    smmLeadRelTable smmLeadRelTableRelatedInfo;
    boolean enableCampaigns = false;
    boolean enableOpportunities = false;

    select firstonly RecId from smmLeadRelTableRelatedInfo
        where smmLeadRelTableRelatedInfo.LeadId == smmLeadTable.LeadId
           && smmLeadRelTableRelatedInfo.RefTableId == tableNum(smmCampaignTable);

    if (smmLeadRelTableRelatedInfo.RecId)
    {
        enableCampaigns = true;
    }

    select firstonly RecId from smmLeadRelTableRelatedInfo
        where smmLeadRelTableRelatedInfo.LeadId == smmLeadTable.LeadId
           && smmLeadRelTableRelatedInfo.RefTableId == tableNum(smmOpportunityTable);

    if (smmLeadRelTableRelatedInfo.RecId)
    {
        enableOpportunities = true;
    }

    campaignTableButton.enabled(enableCampaigns);
    opportunityTableButton.enabled(enableOpportunities);
}

public void init()
{
    if ((SysFormRun::isCalledFromJumpRef(this.args(), fieldNum(smmLeadTable, LeadId)) ||
        element.args().dataset() == tableNum(smmLeadTable) ||
        element.args().dataset() == tableNum(smmLeadRelTable)) &&
        (element.args().caller()))
    {
        jumpToMain = NoYes::Yes;
    }

    super();

    partyForm = new DirPartyFormHandler_Entity(element);

    partyForm.setDatasourceCaller(smmLeadTable_ds);
    partyForm.setDatasourceParty(dirPartyTable_ds);

    // If form is opened from another form
    if (element.args().caller() && element.args().record())
    {
        callerRecord = element.args().record();
    }

    hcmWorkerLookupCurrentCompany = HcmWorkerLookup::newCustomOptions(true, true, true, false, true, true, true, true, true);
}

